window.BEYT_DATA = [{"fa": "بِاسمک اللهم یا فتاح اَبوابُ المنا\nیا غنی الذات یا مَن فیهِ بُرهان الغنا", "tr": "Ey arzu ve ümit kapılarını açan Tanrım, eserime senin isminle başlıyorum. Ey zatı her şeyden müstağni olan, hiçbir şeye ihtiyacı olmayan, kendisinde her şeye sahip ve malik olmanın kemali olan Tanrı, senin isminle başlıyorum."}, {"fa": "یا مفیض الجود یا فیاض آثار الوجود\nیا قدیمُ المُلک یا مَن لَم یغیره الفنا", "tr": "Ey insana ihsanî feyzini veren, ey varlık eserlerini feyizli feyizle halk eden, ey mülkü kadim olan Tanrı, fanilik seni hiçbir zaman değiştiremez."}, {"fa": "یا عمیم اللُّطف یا وهاب لِذّاتُ السُّرور\nیا طَبیبُ القَلب یا حَلّال اشکال العنا", "tr": "Ey lütfu herkese şamil olan, ey insana sevinç lezzetlerini ihsan eden, ey kalplerin derdine derman veren, ey ıztırap müşküllerini halleden Tanrı."}, {"fa": "قَد جنی قَلبی مِنَ الدُّنیا ذُنوبا ثُمَّ تاب\nقَد اتی مُستَغفِرا فَاغفِر لَهُ ما قَد جنا", "tr": "Benim kalbim dünya arzuları uğrunda bir cinayet işledi, sonra tövbe etti. Senin affını isteyerek huzuruna geldi, bu cinayetini afv ü mağfiret et!"}, {"fa": "اَنتَ مَسجودی و مَعبودی فَلَم اَعبُد سواک\nاَنتَ خَلّاقی و رَزّاقی وَ لَم اَعلُم انا", "tr": "Ben sana secde ederim, sana ibadet ederim. Senden başkasına kulluk etmem. Beni yaratan, bana rızık veren sensin. Senden başka tanımam."}, {"fa": "تالَ قَدری مِنکَ مِعراجُ المَعالی واعتلا\nحازَ قَلبی مِنکَ اَسرارُ المَعانی و اغتنا", "tr": "Sen benim kadrimi yükseklere eriştirdin. Kalbime manaların inceliklerini, sırlarını sen açtın. Onu sen böyle mana ile doldurdun."}, {"fa": "قَد شَرَحتُ الصَّدر فَاحلُل مِن لِسانی عُقدة\nنِعمةٌ اَعطیتُها تَمَّم بِتوفیقُ الثَّنا", "tr": "Göğsümü mademki açtın, dilimden de düğümü çöz. Sana karşı hamd ü senaya beni muvaffak ederek verdiğin nimeti tamamla!"}, {"fa": "قَد وَهَبتُ النُّطق قَدَّرنی عَلیٰ حُسن المقال\nحِکمةٌ اخفیتُها فِی الشعر بینها هنا", "tr": "Mademki bana nutuk verdin, beni güzel söz söylemeye muktedir kıl, şiirde gizliliğin hikmetini onda tecelli ettir!"}, {"fa": "اَفضَلُ الاَلطافُ اِدراکُ المَعانی فِی الکَلام\nاحمد الله الذی اعطی فضولی ما عنا", "tr": "Lütufların en büyüğü elimdeki manalara erişmektir. Füzulî’ye istediğini ihsan eden Tanrı’ya hamd ü sena olsun."}, {"fa": "ای ذکر ذوق بخش تو زیب زبان ما\nبی ذکر تو مباد زبان در دهان ما", "tr": "Ey Tanrı, seni anmak bize ne kadar zevk veriyor. O, bizim dilimizin süsüdür. Ağzımızın içinde bu dil, bir an seni anmaktan hâli kalmasın."}, {"fa": "از سکه سعادت توفیق فیض تست\nرایج بهر معامله نقد روان ما", "tr": "Ruhumuzun nakdi üzerinde senin feyzinin verdiği muvaffakıyetin saadet sikkesi vardır. Bu yüzden her muamelede bu nakit revaç bulur."}, {"fa": "آید ز ما همیشه خطا از تو مغفرت\nآنست مقتضای تو اینست شان ما", "tr": "Biz daima hata ederiz, sen daima affedersin. Çünkü senin tanrılığın bunu icap ettirir, bizim şanımıza düşen de günah işlemektir."}, {"fa": "بر حال ما ز غیر تو لطفی نمی رسد\nغیر تو نیست واقف راز نهان ما", "tr": "Bizim hâlimizi görüp acıyan ve lütfeden yalnız sen olabilirsin; çünkü gizli sırrımızı ancak sen bilirsin."}, {"fa": "در راهت از بلا نهراسیم زانکه هست\nسنگ بلای تو محک امتحان ما", "tr": "Senin yolunda beladan korkmayız. Zira senden gelen bela taşı, bizim ayarımızı gösteren mihenk taşıdır."}, {"fa": "تا چند تن دهیم بزجر هوای نفس\nرحمی که گشت طعمه سگ استخوان ما", "tr": "Daha ne kadar nefsimizin hava ve hevesine uyup gideceğiz. Yarabbi! Merhamet et, kemiğimiz köpeğin ağzına düştü."}, {"fa": "نگذاشت درد عشق فضولی ز ما نشان\nاینست در ره طلب او نشان ما", "tr": "Fuzuli, aşk derdi bizi öyle harap etti ki nam u nişanımız kalmadı. Fakat onu sevmek, onu istenilecek yolunda bizim alametimiz işte budur."}, {"fa": "ای بسته دانش تو زبان سوآل ما\nناکرده شرح ، پیشِ تو معلوم حالِ ما", "tr": "Ey Tanrı, senin ilmin bizim dilimizi bağlıyor. Senden bir şey istemiyoruz. Çünkü sen bizim hâlimizi biliyorsun. Söylemeye ne hacet?"}, {"fa": "شام و سحر تصور آثار صنع تست\nنقش نگار خانه خواب و خیال ما", "tr": "Gece gündüz rüyamızın, tahayyülümüzün nigârhânesinde senin yaratıcı kudretinin tasavvurları nakşedilmiştir."}, {"fa": "درک حقیقت تو محالست بر خیال\nاین آرزو کجا و خیال محال ما", "tr": "Hayatın senin hakikatini idrak etmesi muhaldir. Nerede bu arzu, nerede bizim bu hayali muhalimiz."}, {"fa": "داریم حال بد ز مآل فعال بد\nما را بحال ما نگذارد فعال ما", "tr": "Kötü işler yapıyoruz, hâlimiz kötüleşiyor. Bizi kendi hâlimize bırakma, zira günahkâr oluruz."}, {"fa": "روزی که از تو هر عملی را جزا رسد\nتعذیر ما بس است ز تو انفعال ما", "tr": "Her yaptığımız işin karşılığını göreceğimiz o gün, huzurunda duyduğumuz hicap bize kâfi bir cezadır."}, {"fa": "ما را مجال ده که ز ذکر تو دم زنیم\nبهر سخن دمی که نماید مجال ما", "tr": "Yarabbi! Söz söylemeye mecalimiz kalmadığı o anda (ölüm anında) bize kuvvet ihsan et, seni analım."}, {"fa": "اظهار عذر ماست فضولی ز معصیت\nبر روی زرد ما رقم اشک آل ما", "tr": "İsyan ettiğimiz için özür diliyoruz. İşte sarı yüzümüz üzerindeki kanlı gözyaşı bunun ifadesidir."}, {"fa": "زهی فیض وجود از پرتو ذات تو عالم را\nکمال قدر تو برداشته از خاک آدم را", "tr": "Ne ulu peygambersin ki âleme varlık feyzi senin zatının azametindendir. Kadr ü azametinin büyüklüğü Âdem’i topraktan kaldırmıştır."}, {"fa": "شب معراج تعظیم تو ثابت گشته بر انجم\nبچرخ آورده ذوق پای بوست عرش اعظم را", "tr": "Miraç gecesi yıldızların sana ne kadar hürmet ve tazim gösterdiklerini ispat eder. Arş-ı âzam senin ayağını öpmek zevkine ermiştir ve bu zevk ile raks etmektedir."}, {"fa": "رخت کرده شب معراج را از روز روشن تر\nز اشهب بگذرانده عزم اقبال تو ادهم را", "tr": "Senin dirliğin miraç gecesini gündüzden daha aydınlık etmiş. İleriye sürdüğün devlet ve ikbal atı (edhem, yağız at, gece), kır atı (eşhep, gündüz) geride bırakmıştır."}, {"fa": "نباشد هیچ صاحب وحی را توفیق معراجت\nحریم قرب او ادنا مشخص کرده محرم را", "tr": "Hiçbir vahiy sahibine senin gibi miraç muvaffakiyeti nasip olmamıştır. (Ev ednâ) yakınlığının harimini Allah’a mahrem kimdir, onu tanıtır."}, {"fa": "عیار ارتفاع منزلت در راه قرب حق\nترا عرش است چرخ چارمین عیسی مریم را", "tr": "Allah’a yakınlık yolunda senin derecenin yüksekliğini şu bize gösterir: İsa dördüncü göğe kadar çıkabilmiştir, sen arşa kadar yükselmişsin."}, {"fa": "طریق اتباعت راست جنت منزل ادنا\nسبب شد این طریقت ، فتحِ ابوابِ جهنّم را", "tr": "Sana uyanların yolunda cennet en edna bir konak yeridir. Cehennem kapılarının açılmasına sebep senin yolunun kapanmasıdır."}, {"fa": "فضولی را دمادم هست عزم طوف درگاهست\nچه باشد گر دهی انجامی این عزم دمادم را", "tr": "Fuzuli, her an seni, dergâhını tavaf arzusundadır. Bu her an içten gelen isteğe ne olur onu bir muvaffak etsen!"}, {"fa": "بکه نسبت کنم آن سرو صنوبر قد را\nانه اعظم من کل عظیم قدرا", "tr": "O sanavber endamlı serviyi kime benzeteyim? O, kadr ü kıymet itibariyle her büyükten daha büyüktür."}, {"fa": "می نماید بر او نیک بدیهای رقیب\nاین نه نیک است که او نیک نداند بد را", "tr": "Rakibin kötülükleri ona iyi görünüyor. Onun kötüyü iyi bilmemesi iyi bir şey değildir."}, {"fa": "حد اظهار الم نیست مرا پیش بتان\nبکه اظهار کنم این الم بی حد را", "tr": "Güzellerin huzurunda elemimi açığa vurmak benim haddim değildir. Bu hadsiz elemi kime izhar edeyim bilmem ki."}, {"fa": "مسند عشق ز من بیشتر این پایه نداشت\nاشک من سر بگردون سر این مسند را", "tr": "Aşk mesnedi benden evvel bu kadar yüksek değildi. Onun başını benim gözyaşım böyle feleklere kadar yükseltti."}, {"fa": "دود دل کرد سیه روز مرا تا شده ام\nمایل آن مهوش مشکین خط سیمین خد را", "tr": "O ayva tüyleri misk renkli ve kokulu, yanağı gümüş gibi beyaz ay yüzlü güzele âşık olalı beri yanan gönlümden yükselen duman günümü kararttı."}, {"fa": "مقصد ماست درین باغ گل روی تو لیک\nهست صد خار ملامت گل این مقصد را", "tr": "Bu bağda bizim istediğimiz senin gül yüzüne erişmektir. Fakat bu maksat gülünün etrafında yüzlerce melamet dikeni var."}, {"fa": "این چه قید است فضولی که ترا هست ز عشق\nبه ازین نیست کزین در گذرانی خود را", "tr": "Fuzuli, aşk yüzünden ne kadar kayda düşmüşsün. İyisi mi bundan vazgeç."}, {"fa": "مکش بر دیده ای خورشید خاک آن کف پا را\nمکن با خاک یکسان توتیای دیده ما را", "tr": "Ey güneş, o güzelin ayağının toprağını gözüne sürme diye çekme. Gözlerimize nur veren o tûtyâyı toprak ile bir tutma."}, {"fa": "بهر تاری ز جعد سنبلش دل بسته شیدایی\nصبا بر هم مزن جمعیت دلهای شیدا را", "tr": "Onun sümbül saçının her kıvrımına bir şeydâ âşık gönül bağlamıştır. Ey bahar rüzgârı, deli gönüllerin bu huzur ve rahatını (cemiyet) bozup mahvetme."}, {"fa": "دلم را کرد از زهر غم افلاک دوران پر\nبیک جام شکسته کرد خالی هفت مینا را", "tr": "Devran, feleklerin gam zehrini gönlüme doldurdu. Bir kırık kadehe yedi şişe (yedi felek)yi de boşalttı."}, {"fa": "ملک را نیست چون خورشید رخسار تو زیبایی\nعیانست این نمی پوشد کسی رخسار زیبا را", "tr": "Meleklerin yüzü, senin güneş yüzün kadar güzel değildir. Bu apaçıktır. İsbata hacet yok. Çünkü kimse güzel yüzünü gizlemez."}, {"fa": "تو سایه بر زمین انداختی یا دید خورشیدت\nترا در بر گرفت و بر زمین انداخت عیسا را", "tr": "Ya sen gölgeni yere saldın; yahut güneş güzelliğini görür görmez seni kucağına alıp İsa’yı yere attı."}, {"fa": "تمنای بقای عمر در دل داشتم اما\nبرون کرد آرزوی تیغت از دل این تمنا را", "tr": "Gönlümde ömrün bekası arzusunu taşıyıp duruyordum. Fakat senin kılıcına karşı duyduğum iştiyak, bu isteği gönlümden çıkardı."}, {"fa": "فضولی زین سبب خونابه را در دیده جا کردم\nکه می‌آرد به خاطر هر دم آن گلبرگ رعنا را", "tr": "Fuzuli, gözümde kanlı gözyaşına yer vermekten maksadım şudur: Her dem sevgilimin o güzel gül yaprağına benzeyen dudağı bana hatırlatıyor."}, {"fa": "چو از غم کنم چاک پیراهنم را\nز مردم کند اشک پنهان تنم را", "tr": "Dert içinde gömleğimi parçaladığım zaman, gözyaşı vücudumu örtüp çıplak görünmeme mâni olur."}, {"fa": "چه سان با قد خم کنم عزم کویش\nگرفتست خار مژه دامنم را", "tr": "Bu bükülmüş belimle nasıl onun diyarına gidebilirim. Zira kirpiklerim diken diken olmuş, eteğimi tutmuştur."}, {"fa": "غمت دانه ها می فشاند ز چشمم\nبباد فنا می دهد خرمنم را", "tr": "Senin aşkın gözümden daneler saçıyor, harmanımı fena rüzgârına veriyor."}, {"fa": "نیامد ز دست تو ای من غلامت\nکه در طوق ساعد کشی گردنم را", "tr": "Ey kölesi olduğum dilber, bir kere elinden gelmedi ki kolunu boynuma dolayasın."}, {"fa": "ز هر سو ره آرزو بست بر من\nسرشکم که بگرفت پیرامنم را", "tr": "Dört yanımı çeviren gözyaşı, bana her taraftan murat yolunu kesti."}, {"fa": "مبین محتسب تند در ساغر می\nمکن تیره آیینه روشنم را", "tr": "Ey muhtesip, şarap kadehine sert sert bakıp parlak aynamı bulandırma."}, {"fa": "ز غم مرده ام ماتم خویش دارم\nفضولی ملامت مکن شیونم را", "tr": "Fuzuli, feryadımı ayıplama; ‘beri aşk ıstırabında’ ölmüşüm. Kendi matemimi tutuyorum."}, {"fa": "ز ضعف تاب تردد دگر نماند مرا\nخوشم که ضعف ز سرگشتگی رهاند مرا", "tr": "O kadar zaifim ki artık sevgilimin diyarına gidip gelmeye, peşinde koşup durmaya takatim kalmadı. Memnunum; çünkü bu zaaf beni avarelikten kurtardı."}, {"fa": "فغان که آرزوی وصل آن دو چشم سیاه\nچو میل سرمه بخاک سیه نشاند مرا", "tr": "Ah ne yazık ki sevgilimin o iki siyah gözüne ulaşmak arzusu beni sürme mili gibi kara toprağa gömdü."}, {"fa": "تنم ز آتش دل می گداخت گر شب غم\nسرشگ آب بر آتش نمی فشاند مرا", "tr": "Gözyaşı, gönlümdeki ateşe su serpmeseydi, ıstırap gecesi vücudum bu ateşle muhakkak erirdi."}, {"fa": "جهانی از پی نظاره بر سرم شده جمع\nنگه کنید که سودا کجا رساند مرا", "tr": "Beni seyretmek için cihan başıma toplanmış. Bakın, sevda beni ne hallere koydu."}, {"fa": "درین امید که صیدم کند سگ در او\nهوس چون آهوی وحشی بسی دواند مرا", "tr": "Onun kapısındaki köpekler tarafından avlanmak hevesi beni vahşi ceylanlar gibi ne kadar koşturdu."}, {"fa": "میان مردمم این آبرو بس است که دوش\nپریوشی سگ درگاه خویش خواند مرا", "tr": "Dün gece bir peri yüzlü güzel bana: “Sen benim kapımın köpeğisin!” dedi. İnsanlar arasında bu şeref bana kâfidir."}, {"fa": "من گدا بکه گویم فضولی این غم دل\nکه همچو سگ ز در او رقیب راند مرا", "tr": "Fuzuli, rakib beni köpek gibi onun kapısından kovdu. Ben fakir (dilenci) bu derdimi kime söyleyeyim."}, {"fa": "چه گونه فاش نگردد غم نهانی ما\nبشرح حال زبانیست بی زبانی ما", "tr": "Gizli derdimiz nasıl faş olmaz. Sükûtumuz (dilsizliğimiz) bizim hâlimizi açığa vuran bir dildir."}, {"fa": "برون مباد زمانی ز جان ما غم یار\nکه در بلا غم یارست یار جانی ما", "tr": "Yârın derdi bir zaman gönlümüzden (canımızdan) çıkmasın. Zira bela zamanında yârın derdi bizim yâri canımızdır."}, {"fa": "در سرشک بپای تو ریختیم و خوشیم\nکه صرف راه تو شد نقد زندگانی ما", "tr": "Gözyaşı incisini senin ayağına döktük ve memnunuz. Zira hayatımızın nakdi senin yoluna sarf oldu."}, {"fa": "شکست بار غمت قد ما چه سنگ دلی\nکه هیچ رحم نکردی به ناتوانی ما", "tr": "Ahının yükü belimizi büktü (kırdı). Ne taş yüreklisin. Bu bitkin hâlimize hiç acımıyorsun."}, {"fa": "زمانه دشمن ما گشت در غمت گویا\nکه رشک برد بر ایام شادمانی ما", "tr": "Senin aşkının yüzünden çektiğimiz ıstırap âlemi bize düşman etti. Sanki felek bu sevinçli günlerimize haset etti."}, {"fa": "شدیم سالک راه وفات لیک چه سود\nکه عمر تاب ندارد به همعنانی ما", "tr": "Senin sevgine sadık kalmak için azmettik (vefa yoluna düştük); fakat ne faide, ömür bizimle bu yolda yürüyemedi (bizimle atbaşı beraber gidemedi)."}, {"fa": "رسیده ایم فضولی ز فیض عشق کام\nبس است درد و غم اسباب کامرانی ما", "tr": "Fuzuli, aşkın feyzi ile murada erişmişiz. Bizi bahtiyar etmek için derd ü gam kâfi değil mi?.."}, {"fa": "بخاک ره کشیدم صورت جسم نزارم را\nبدین صورت مگر بوسم کف پای نگارم را", "tr": "Yolunun toprağı üzerine bu zaif cismimin resmini çizdim. Belki bu suretle sevgilimin ayağını öpebilirim."}, {"fa": "غبار رهگذارم کرد شوق امید آن دارم\nکه گاهی خیزم و گیرم رکاب شهسوارم را", "tr": "Aşk beni onun yolu üzerinde bir toz hâline getirdi. Şu ümidim var ki ara sıra kalkıp o şehsüvarımın dizginini tutabilirim."}, {"fa": "شدم خاک ره غم اشک خواهد ریخت بر حاکم\nبهر چشمی که دوران توتیا سازد غبارم را", "tr": "Gam yolunun toprağı oldum. Devran benim tozumdan kimin gözüne sürme çekerse o benim hâlime gözyaşı dökecektir."}, {"fa": "ره رسوایی از فرهاد و مجنون یافتم خالی\nز خار و خس زمانه پاک کرده رهگذارم را", "tr": "Aşk yüzünden rüsva olmak yolunda Ferhat ve Mecnun’a yaslanmadım. Zamane yolumu temizlemiş, çörçöp bırakmamış."}, {"fa": "غبار آستانت گریه ام را می دهد تسکین\nازین به توتیایی نیست چشم اشکبارم را", "tr": "Ancak senin eşiğinin tozu gözyaşımı dindiriyor. Ağlayan gözlerime ondan iyi sürme (tûtyâ) yoktur."}, {"fa": "حذر کن ای فلک از آه و اشک من مکن کاری\nکه ناگه بر کشم از قهر تیغ آبدارم را", "tr": "Ey felek, ahımdan ve gözyaşımdan hazer et. Sakın bir şey yapayım deme; zira bu parlak (âbdâr) kılıcı ansızın çeker, seni kahrederim."}, {"fa": "فضولی قصه بیداد آن گلرخ چه می خوانی\nچرا نومید می‌سازی دل امیدوارم را", "tr": "Fuzulî, o gül yanaklının zulmünden bahsedilmesini niye istiyorsun; bu ümitli gönlümü niye ümitsizliğe düşürüyorsun?.."}, {"fa": "نهان می‌سوخت چون شمع آتش دل رشتهٔ جان را\nزبان حالم آخر کرد روشن سوز پنهان را", "tr": "Ben muma benziyorum; gönlümün ateşi içimdeki can riştesini gizlice yakıyor. Bu gizli yanışımı da hâlim (hâl dilim) açığa vuruyor."}, {"fa": "ز رشک آن که دامن روی بر پای تو می‌مالد\nبه دامن می‌رسانم متصل چاک گریبان را", "tr": "Eteğin senin ayağına yüz sürüyor. Ben bunu öyle kıskanıyorum ki ıstırabımdan yakamı eteğime kadar parçalıyorum."}, {"fa": "نظر بر حال من از چشم بیمارت عجب نبود\nکه اهل درد می‌دانند قدر دردمندان را", "tr": "Hasta, mahmur gözün benim hâlime bakıp acıyorsa bu şaşılacak bir şey değildir. Dertlilerin kadrini ancak hasta dertli olanlar bilir."}, {"fa": "به خوناب جگر آغشته‌ام چون لاله سر تا پا\nاثر بینید داغ عشق آن گلبرگ خندان را", "tr": "Gelincik gibi baştan ayağa ciğer kanına bulanmışım. O açılmış (handân) gül yaprağına âşığım. Bu aşk yarasının eserine bakınız."}, {"fa": "دلی شد بسته هر تار زلفت حسبة لله\nکره مفکن برو بر هم مزن جمعی پریشان را", "tr": "Zülfünün her teline bir gönül bağlanmıştır. Allah aşkına onu örüp de muztarip, perişan bir cemiyeti perişan etme."}, {"fa": "ز خط بر مصحف حسنت فزون شد رغبت دل‌ها\nکه با اِعراب طفلان خوب‌تر خوانند قرآن را", "tr": "Güzelliğinin mushafı yüzünde ayva tüyleri (hat) belirince daha güzelleşti, gönüllerin ona rağbeti arttı. Zira çocuklar harekeli Kur’an’ı daha güzel okurlar."}, {"fa": "فضولی صفحه جان را ز عکس دانهٔ خالش\nچنان پر کن که مطلق جا نماند داغ هجران را", "tr": "Fuzli, onun yanağındaki benin aksi ile can sahifesini öyle doldur ki hicran dağına hiçbir yer kalmasın."}, {"fa": "با خود ای جان در غمش همدم نمی خواهم ترا\nبی ثباتی محرم این غم نمی خواهم ترا", "tr": "Ey can, onun aşkı yüzünden çektiğim ıstıraba ortak olmanı istemiyorum. Sen sebatsizsin. Bu ıstıraba mahrem olmanı istemiyorum."}, {"fa": "جان من از طعنه اغیار خود را می کشم\nغیرتی دارم که با خود هم نمی خواهم ترا", "tr": "Ey canım, ağyar bana çok hakaret ediyor. Dayanamıyorum. Kendimi öldüreceğim. Onun yolunda benden başka kimsenin kurban olmasını da çekemiyorum. Kıskanıyorum. Onun için benimle beraber olmanı istemiyorum."}, {"fa": "ای دل از دیوانه بی قید باید احتراز\nدور از آن گیسوی خم بر خم نمی خواهم ترا", "tr": "Ey gönül, zincire vurulmamış bir deliden çekinmek lazımdır. O kıvrım kıvrım saçtan uzak olmanı istemiyorum."}, {"fa": "نشأه فکر رخش از ذوق دیدن نیست کم\nبیش ازین این دیده پر نم نمی خواهم ترا", "tr": "Onun güzel yanaklarını düşünmek neşesi, onları görmek zevkinden daha az değildir. Ey yaşlı gözlerim, artık sizlerimde istemiyorum."}, {"fa": "آفرین ای اشک از خاک رهم برداشتی\nقدر من از تست عالی کم نمی خواهم ترا", "tr": "Aferin ey gözyaşı, beni topraktan kaldırdın. Beni yükselten sensin. Seni hakir (az) görmek istemiyorum."}, {"fa": "می کنی در عشق آن ترسا ز مردن منع من\nگر مسیحایی تو ای همدم نمی خواهم ترا", "tr": "O Hıristiyan güzelin aşkı uğrunda ölmekten beni menediyorsun. Ey arkadaş, Hazreti İsa dahi olsan seni istemiyorum."}, {"fa": "مگذران در دل فضولی رغبت قید خرد\nمبتلای محنت عالم نمی خواهم ترا", "tr": "Fuzuli, gönlünden akla rağbet duygusunu geçirme. Seni âlemin derdine müptela görmek istemiyorum."}, {"fa": "شنیده صبحدم از جور گل افغان بلبل را\nبدندان پاره پاره ساخته شبنم تن گل را", "tr": "Çiğ tanesi sabahleyin gülün cevrinden bülbülün feryadını işitip dişleriyle gülün tenini parça parça etti."}, {"fa": "چو گیرم کاکلَش را، تا کِشم سویِ خُودم ، آن مه،\nبقصد دوری من می گشاید عقد کاکل را", "tr": "Beni kendisine çeksin diye kakülüne sarıldığım zaman o ay yüzlü güzel beni uzaklaştırmak için örülmüş saçını çözüyor."}, {"fa": "صبا را جویبار از موج در زنجیر می دارد\nبجرم آن که با زلفت برابر گفت سنبل را", "tr": "Sümbülü saçıyla beraber tuttuğu için ırmak, bahar rüzgârını dalga zincirine vuruyor."}, {"fa": "لباس عاریت را اعتباری نیست ای منعم\nز گلبن کم نه بر باده ده رخت تجمل را", "tr": "Ey zengin, eğreti elbiseye itibar edilmez. Bir gül fidanından da aşağı değilsin ya, bu süslü elbiseleri rüzgâra ver gitsin."}, {"fa": "چه جویم التفات از گلرخی کز غایت شوخی\nز اسباب کمال حسن می داند تغافل را", "tr": "Ben o gül yanaklıdan hiç iltifat bekler miyim? Son derece şımarık. Âşıkları görüp de görmemezlikten gelmek, güzelliğini tamamlar sanıyor."}, {"fa": "نه ای عاشق ، اگر فکرِ نجات از قیدِ غم داری،\nچه نسبت با اسیر عشق تدبیر و تأمل را", "tr": "Eğer gam kaydından kurtulmayı düşünüyorsan sen âşık değilsin. Aşk esiri, felaket karşısında bir tedbir alır, bir şey düşünebilir mi?"}, {"fa": "فضولی بی توکل راه دشوارست بر مقصد\nمده گر طالبی از دست دامان توکل را", "tr": "Fuzuli, mütevekkil olmayan insan maksadına güç erişir. Eğer hakikate erişmek istiyorsan tevekkül eteğini elden bırakma."}, {"fa": "چنان بنهفته ضعف تن مرا لطف بدن او را\nکه رفته عمرها نی او مرا دیده نه من او را", "tr": "Vücudumun zayıflığı beni, bedeninin letafeti de onu öyle gözden saklıyor ki ömürler geçtiği hâlde ne o beni görüyor ne de ben onu."}, {"fa": "ز درد عشق و داغ هجر می نالم خوش آن رندی\nکه نی اندیشه جانست و نی پروای تن او را", "tr": "Aşk derdinden, hicran yarasından ağlayıp duruyorum. Ne bahtiyardır o rind ki ne canını düşünür ne de ten ile alakası vardır."}, {"fa": "غمت در سینه دارم شمع را کی سوز من باشد\nندارد جسم او جانی چه باک از سوختن او را", "tr": "Sinemde senin aşkının ıstırabı vardır. Mum bile benim gibi yanmaz. Zira onun cismi candan mahrumdur; yanmaktan ne korkusu olabilir ki."}, {"fa": "ندارد بر زبان جز راز عشقت شمع می دانم\nکه آخر گشته بیرون می برند از انجمن او را", "tr": "Mumun dilinde (alevinde) senin aşkının sırrından başka bir şey yoktur. Bunu iyice biliyorum. Zira nihayet onu söndürüp meclisten dışarı atarlar."}, {"fa": "بکوه بیستون نقشی که دیدی نیست جز شیرین\nزده بر سنگ از رشک جمالت کوهکن او را", "tr": "Bîsütun dağında gördüğün resim Şirin’in başka bir şey değildir. Ferhat, senin güzelliğini görünce hasedinden onu taşa çalmıştır."}, {"fa": "بت است آن سنگدل این بس کمال معجز عشقم\nکه می آرم باظهار تظلم در سخن او را", "tr": "O taş yürekli dilber bir puttur. Aşkımın mucizesindeki kemale bakın ki yalvara yalvara onu bile dile getiriyorum."}, {"fa": "فضولی سوخت بر تن داغ‌های تازه سر تا پا\nکه نشناسند در کوی تو از داغ کهن او را", "tr": "Fuzuli, vücudumda baştan ayağa yeni yeni yaralar açıldı. Öyle ki senin diyarında artık onu eskiden olduğu gibi yaralarından tanımıyorlar."}, {"fa": "عشقت از دایره عقل برون کرد مرا\nداخل سلسله اهل جنون کرد مرا", "tr": "Aşkın beni akıl dairesinden çıkarıp delilerin silsilesine (zincirine) dâhil etti."}, {"fa": "در غم عشق بتان هیچ کسی چون من نیست\nنظری کن که غم عشق تو چون کرد مرا", "tr": "Güzel putları sevenler içinde hiç kimse benim gibi muztarip değildir. Bir bak, senin aşkın beni ne hâle koydu."}, {"fa": "من نبودم به غم عشق چنین به طاقت\nکمی لطف تو بسیار زبون کرد مرا", "tr": "Ben aşkın elemleri altında bu derece ezilmiyordum. Senin lütfunun azlığı beni çok zebun etti."}, {"fa": "بامیدی که مگر طعنه زنان نشناسند\nشادم از اشک که آغشته بخون کرد مرا", "tr": "Gözyaşım beni kana boyadı. Fakat ben seviniyorum; belki bana ta’n edenler beni bu hâl ile tanıyamazlar."}, {"fa": "رسته بودم ز گرفتاری شیرین دهنان\nباز لعل تو مقید بفسون کرد مرا", "tr": "Bal dudaklılara esir olmaktan kurtulmuştum; senin la‘l dudağın beni yine o efsuna düşürdü."}, {"fa": "کم نشد بی لب شیرین تو جان کندن من\nوه که این شیوه ز فرهاد فزون کرد مرا", "tr": "Tatlı (şirin) dudağından ayrı benim ıstırabım azalmadı. Bilakis ne yazık ki bu hâl beni Ferhat’tan da üstün etti. Çünkü Ferhat, Şirin’in resmini Bîsütun dağına nakşetmiş ve onunla müteselli olmuştu."}, {"fa": "ز ازل در دل من بود فضولی غم عشق\nفلک آشفته بدینسان نه کنون کرد مرا", "tr": "Fuzuli, kalbimde bu aşk derdi ezelden beri vardı. Fakat felek şimdi beni bu derece perişan etti."}, {"fa": "عشق حیران بتان سیمبر دارد مرا\nچون بت از حالی که دارم بی خبر دارد مرا", "tr": "Aşk beni gümüş sineli güzellere (büt) hayran ediyor. Beni humma (teb) ateşleri içinde kendini kaybeden bir hastaya döndürüyor."}, {"fa": "مردم چشم تو دارد فکر صد آزار دل\nهر چه بر دل می رساند در نظر دارد مرا", "tr": "Senin gözbebeğin (merdüm-i çeşm) yüzlerce gönlü incitmek fikrindedir. Gönlüne gelen her düşüncede daima beni göz önünde tutar."}, {"fa": "نیست از مهر این که خونم را نمی ریزد فلک\nاز برای روزگاری زین بتر دارد مرا", "tr": "Felek benim kanımı dökmüyor, fakat bu beni sevdiğinden değildir. Beni bundan daha kötü bir güne saklıyor."}, {"fa": "ساقیا سرمستیم از نشأه جام تو نیست\nاینچنین دیوانه سودای دگر دارد مرا", "tr": "Sâkî, sarhoşluğum senin verdiğin kadehin neşvesinden değildir. Beni başka bir sevda böyle divane bir hâle getirdi."}, {"fa": "بر رهش بنشسته ام چون کودکان چابک سوار\nدر رسد با جلوه و از خاک بر دارد مرا", "tr": "Küçük çocuklar gibi onun geçeceği yol üzerine oturmuşum. Çevik bir süvariye benzeyen sevgilim çıkagelip beni yerden kaldırır."}, {"fa": "در روم در خانه بندم درش را چون حباب\nتا بکی چون باد دوران در بدر دارد مرا", "tr": "Bir eve girip habab (su kabarcığı) gibi kapısını kapayayım. Devran ne zamana kadar beni rüzgâr gibi kapı kapı gezdirecek (derbeder edecek)."}, {"fa": "همچو جام می فضولی چون نریزم اشک آل\nآرزوی لعل او خونین جگر دارد مرا", "tr": "Fuzuli, nasıl şarap kadehi gibi kanlı gözyaşları dökmeyeyim. Onun la‘l dudağına hasret çeke çeke ciğerim kan oldu."}, {"fa": "ساقیا می ده که حرفی ز آن دهان گویم ترا\nتا نکردم مست کی راز نهان گویم ترا", "tr": "Ey sâkî, şarap ver de sana o dudaktan bahsedeyim. Sarhoş olmadıkça sana gizli sırrı nasıl söyleyebilirim."}, {"fa": "بس که از حیرت بود هر لحظه ام حال دگر\nحیرتی دارم که حال خود چه سان گویم ترا", "tr": "Hayret beni her lahza hâlden hâle koyuyor. Hâlimi sana nasıl anlatayım. Hayretteyim."}, {"fa": "کی توانم گفت حوری در لطافت یا ملک\nهر چه نتوان دید چون باشد که آن گویم ترا", "tr": "Güzelliğini tasvir için sana huri veya melek nasıl diyebilirim. Seni her görülmeyen şeye nasıl benzeteyim?"}, {"fa": "ساعتی بر چشمه چشمم نمی گیری قرار\nزین روش می زیبد ار سرو روان گویم ترا", "tr": "Gözümün pınarında bir an durup dinlenmiyorsun. Bu hâlinden dolayı sana servi-revân desem yaraşır."}, {"fa": "الفت جان را ثباتی نیست می ترسم ز هجر\nجان من از دل نمی آید که جان گویم ترا", "tr": "Can, korkarım hicran ile devamlı surette arkadaşlık edemez. Canım (sevgilim), sana can demek içimden gelmiyor."}, {"fa": "شمع من یاد تو تنها نیست دور از طعنه\nمی کنم ذکر بتان تا در میان گویم ترا", "tr": "Ey benim gözümün nuru (şem‘im), yalnız senin ismini anınca el bana ta‘n ediyor. Güzellerin adlarını anıyorum ki arada senin adını da söyleyebileyim."}, {"fa": "تا ز گردون نگذرد شبها فضولی ناله ات\nسگ به از من گر سگ آن دلستان گویم ترا", "tr": "Fuzuli, geceler feryadın felekleri aşmadıkça sana o sevgilinin köpeği dersem köpekten alçak olayım."}, {"fa": "خاک در تو کحل بصر کرده ایم ما\nوز هر که جز تو قطع نظر کرده ایم ما", "tr": "Senin kapının toprağını biz gözümüze sürme etmişiz. Senden başka herkesten alakamızı kesmişiz (kat‘-ı nazar)."}, {"fa": "ما را چه باک در ره عشق تو از رقیب\nتدبیر او بآه سحر کرده ایم ما", "tr": "Senin aşkının yolunda rakipten hiç korkumuz yoktur. Seher vakti bir âh eder, onun hesabını görürüz."}, {"fa": "خم گشته ایم تا نرباید ز ما فلک\nخاکی که از در تو بسر کرده ایم ما", "tr": "Kapında secde ederken alnımıza konan (ıstırap ile başımıza serptiğimiz) toprağı felek kapmasın diye iki kat olmuş, öyle geziyoruz."}, {"fa": "تا رخنها ز تیغ جفای تو یافتست\nاز سر هوای غیر بدر کرده ایم ما", "tr": "Senin cefa kılıcın başımızda yarıklar açtı. O yarıklardan senden başkalarına karşı beslediğimiz sevgi (hava) çıkıp gitti."}, {"fa": "تا بیشتر برد ز رهت گرد در سجود\nرخساره تر بخون جگر کرده ایم ما", "tr": "Yüzümüzü ciğerimizin kanı ile ıslattık ki yolunda secde ettiğimiz zaman yüzümüze daha fazla toz yapışsın."}, {"fa": "سر نمی دهیم بهر تو ما را مباد سر\nکه غیر ازین خیال دگر کرده ایم ما", "tr": "Senin uğrunda başımızı veririz. Eğer bundan başka bir şey düşünüyorsak başsız kalalım."}, {"fa": "اول گذشته ایم فضولی ز کام دل\nوانگه بکوی عشق گذر کرده ایم ما", "tr": "Fuzuli, biz evvelâ gönül arzusundan geçtik, ondan sonra aşk diyarına gelebildik."}, {"fa": "بهار آمد صدایی برنمی‌آید ز بلبل‌ها\nمگر امسال رنگ دلربایی نیست در گل‌ها", "tr": "Bahar geldi, hâlâ bülbüllerden ses seda yok. Galiba bu sene güllerde gönül çeken renk yok."}, {"fa": "گل آمد نیست میل سیر گلشن نازنینان را\nپریشان کرد گل‌های چمن را این تغافل‌ها", "tr": "Güller açtı, nazlı güzeller gülistanlara çıkıp gezmiyorlar. Onların bu aldırmazlıkları çimen güllerini perişan etti."}, {"fa": "چو رغبت نیست در عاشق چه سود از آنکه محبوبان\nبرافروزند عارض‌ها برافشانند کاکل‌ها", "tr": "Âşık rağbet etmedikten sonra mahbublar yanaklarını parlatmışlar, kaküllerini dağıtmışlar, ne çıkar."}, {"fa": "درین موسم چرا دل‌ها مقید نیست در گلشن\nمگر زنجیرهای زلف نگشادند سنبل‌ها", "tr": "Bu mevsimde niye gülistana gönül bağlanmıyor, acaba sümbüller zülüflerinin zincirlerini açmadılar mı?"}, {"fa": "چو غنچه صد گره دارد دل از غم وین غم دیگر\nکه دوران در گشاد هر گره دارد تعلل‌ها", "tr": "Gönül gonca gibi yüzlerce gam düğümüyle düğümlenmiş. Devran her düğümü açarken ne kadar nazlanıyor; bu da ayrı bir dert."}, {"fa": "ازآن بگرفت در بر آب را گلشن به صد عزت\nکه پیدا کرد از اقبال او چندین تجمل‌ها", "tr": "Gülşen suyu bin izzet ü ikramla göğsünde tutuyor. Çünkü onu bu kadar süsleyen o suyun gelişi (ona yönelmesi)dir."}, {"fa": "فضولی رهگذار عشقبازی صد خطر دارد\nشروع این طریق صعب را باید تأمل‌ها", "tr": "Fuzuli, âşıklık yolunda yüzlerce tehlike vardır. Bu güç yola girmek için insan çok düşünmelidir."}, {"fa": "روزی که پیش خویش نبینم حبیب را\nدارم هزار شوق که بینم رقیب را", "tr": "Sevgilimi gözümün önünde görmediğim gün rakibi görmeye can atarım. Gülün önünde dikeni görüyor, haset bülbülü nasıl muztarip etmesin."}, {"fa": "در پیش گل مشاهده خار می کند\nچون رشک مضطرب نکند عندلیب را", "tr": "Biliyorum ki aşk derdine deva yoktur. Niye beyhude yere tabibin başını ağrıtayım?"}, {"fa": "دانسته ام که عارضه عشق بی دواست\nبیهوده درد سر چه رسانم طبیب را", "tr": "Sevgilinin visalinden ümidim kesilmemiştir; lâkin çok sabırsızım, hiç tahammülüm kalmadı."}, {"fa": "امید نیست منقطع از وصل دوست لیک\nصبری نمانده است من ناشکیب را", "tr": "Dedim ki: Gönlüm senin çenenden kuvvet buldu. Güldü; “Elmanın faydası budur.” dedi."}, {"fa": "گفتم دل من از ذقنت قوتی گرفت\nخندید و گفت منفعت اینست سیب را", "tr": "Yârın visalinin sofrası esirgemeden ihsan edilen bir feyizdir. Yarabbi, ben nasipsize ondan bir nasip ihsan et."}, {"fa": "از خوان وصل یار که فیضیست بی دریغ\nیارب نصیب بخش من بی نصیب را", "tr": "Fuzuli, sevgilinin diyarına gitmekten başka bir dileğimiz yoktur. Garip, vatanının toprağını bütün âleme tercih eder."}, {"fa": "گفتم جان دهم بتو جانی نداشتم\nدادم فریب آن صنم دلفریب را", "tr": "[Tercüme Eksik]"}, {"fa": "جز کوی یار نیست فضولی مراد ما\nخاک وطن به از همه عالم غریب را", "tr": "[Tercüme Eksik]"}, {"fa": "تحیر بست در شرح غم عشقت زبانم را\nچه گویم بر تو چون ظاهر کنم راز نهانم را", "tr": "Hayretten dilim tutuldu, sana aşkımı anlatamıyorum. Sana ne söyleyeyim; gizli sırrımı nasıl açayım?.."}, {"fa": "به سوزِ دل ز وصلَت چاره ای جُستم ، ندانستم،\nکه آتش بیش خواهد سوخت از نزدیک جانم را", "tr": "Sensiz içim yanıyor, buna visalinle çare bulmak istedim. Fakat bilmedim ki ateş, yakma gelince canımı daha çok yakacak."}, {"fa": "شدی غایب ز چشمم ، شد دلم صد پاره از غیرت،\nکز آن ، هر پاره جایی می رود از پِی گمانم را", "tr": "Gözümden kayboldun, gönlüm parçalandı. Her parçası seni bulmak ümidiyle şüphenin peşine düşmüş, bir tarafa koşup duruyor."}, {"fa": "ز غیرت سوخت ای خورشید جانم رحم بر من کن\nبهر خاکی میفکن سایه سرو روانم را", "tr": "Ey güneşlere benzeyen sevgilim, kıskançlıktan canım yanıyor. Bana acı; öyle her toprağın üstüne servi-revânımın gölgesini serme."}, {"fa": "رقیبی را سگ خود خواند یارم جای آن باشد\nکه سوزد آتش این رشک مغز استخوانم را", "tr": "Sevgilim bir rakibe “sen benim köpeğimsin” dedi. Buna öyle haset ettim ki kemiğimin içindeki ilik yandı."}, {"fa": "ز ذوق درد و داغش می کند آگه ازانست این\nکه با جان حزین ربطیست جسم ناتوانم را", "tr": "Mahzun canım ile dermansız vücudum arasında bir rabıta olduğu için onun aşkının ıstırabı ile vücudumda açılan yara canıma, canımın duyduğu dert de vücuduma zevk veriyor. Yani bunlar duydukları zevkten birbirlerini haberdar ediyorlar."}, {"fa": "فضولی کی توانم رست در عالم ز رسوایی\nمگر در تن کمال ضعف ره بندد فغانم را", "tr": "Fuzuli, ben bu âlemde rüsvâlıktan kurtulamam. Meğer ki o kadar zayıf olayım ki bu zaaf feryadımın yolunu kessin (feryadıma mecal kalmasın)."}, {"fa": "ز آتشین‌رویی جدا می‌افکند دوران مرا\nچون شرر البته خواهد کشت این هجران مرا", "tr": "Devran beni bir ateş yüzlü dilberden ayırdı. Bu ayrılık beni elbette bir kıvılcım gibi öldürecektir (söndürecektir)."}, {"fa": "کاش خون دیده بنشاند غبار هستیم\nچند دارد گرد باد آه سرگردان مرا", "tr": "Bu âh kasırgası ne zamana kadar beni avare, sergerdan edecek? Keşke kanlı gözyaşlarım bu varlığımın tozunu bastırsa."}, {"fa": "آنچنین از دیده مردم نمی‌کردم نهان\nگر نبودی جوهر شوق لبت در جان مرا", "tr": "Dudağının aşkının cevheri canımda olmasaydı insan gözünden böyle nihan olmazdım."}, {"fa": "از پری‌رخساره دارم درون دل غمی\nوه که خواهد کرد رسوا این غم پنهان مرا", "tr": "Gönlümün içinde bir peri yanaklının aşkını gizliyorum. Eyvah! Bu gizli gam beni âleme rüsvâ edecek."}, {"fa": "تا کجا خواهد شکستم داد باز افکند دور\nچرخ چون تیر از کمان ابروی جانان مرا", "tr": "Çarh yine beni ok gibi sevgilinin kaşının yayından fırlattı (uzağa attı). Bakalım beni ne dereceye kadar daha zebun edecek."}, {"fa": "پیش خوبان گر بدی گوید رقیب از من چه باک\nخوب می‌دانند در راه وفا خوبان مرا", "tr": "Rakip, güzellerin yanında beni kötülerse hiç üzülmem. Güzeller, vefa yolunda beni iyi bilirler."}, {"fa": "بود پنهان درد عشق من فضولی مدتی\nکرد رسوا پیش مردم دیده گریان مرا", "tr": "Fuzuli, aşkımın derdi bir müddet gizli kalmıştı; gözüm halk içinde beni rüsvâ etti."}, {"fa": "از زبانت می رسد هر لحظه آزاری مرا\nمی خلد هر دم بدل زان برگ گل خاری مرا", "tr": "Dilin beni her lahza incitiyor. O gül yaprağından her dem bana bir diken batıyor."}, {"fa": "می تواند کرد پنهان از رقیبم ضعف تن\nگر نسازد فاش هر دم ناله زاری مرا", "tr": "Her an bir feryat, bir inilti benim varlığımı ifşa etmese vücudumun zayıflığı beni rakibin gözünden gizleyebilir."}, {"fa": "زار مردم در غم تنهایی و ممکن نشد\nاین که بیند زاریم یاری کند یاری مرا", "tr": "Kimsesizlik elemi içinde inleye inleye öldüm. Bir dost, benim hâlimi görüp de bana dostluk göstermedi."}, {"fa": "در حریم الفتم آزادگان را راه نیست\nمن گرفتارم نباید جز گرفتاری مرا", "tr": "Hür insanlarla uyuşamam, onlarla dost olamam. Ben esirim. Beni anlayacak ancak bir esir olabilir."}, {"fa": "سوختی ای شمع تا در بزم او ره یافتی\nبنده طور توام آموختی کاری مرا", "tr": "Ey mum, onun bezmine bir yol bulup girebilmek için yandın. Bu buluşuna, bu tavrına hayran oluyorum; bana bir iş öğrettin."}, {"fa": "هر کجا افتاده ام افکنده فرشی زیر من\nنیست در روی زمین جز سایه غمخواری مرا", "tr": "Nereye düşsem altıma bir yaygı seriyor; yeryüzünde gölgemden başka bir dert ortağım yok."}, {"fa": "چرخ را با من فضولی هست مهری زین سبب\nمی کند هر دم اسیر ماه رخساری مرا", "tr": "Fuzuli, çarh bana karşı bir sevgi (mihr) besliyorsa bu, her an beni bir ay yüzlüye esir ettiği içindir."}, {"fa": "کرد عشق ای خون دل در کوی او رسوا مرا\nجامه پوشان که نشناسد کسی آنجا مرا", "tr": "Ey gönlümden akan kan, aşk onun diyarında beni rüsvâ etti. Bana bir elbise giydir ki orada kimse beni tanımasın."}, {"fa": "چند در کوی تو باشد همنشین من رقیب\nبرق آهم کاش یا او را بسوزد یا مرا", "tr": "Senin diyarında ne zamana kadar rakip benimle beraber bulunacak? Bu âhımın yıldırımı ne olur, ya onu yaksa ya beni."}, {"fa": "کلخنی شد منزلم بی آتش رخسار او\nعاقبت بنشاند بر خاک سیه سودا مرا", "tr": "Onun ateşler saçan yanağından uzak kaldım. Evim adeta bir külhan, ıstırap içinde yanıyorum. Sevda nihayet beni kara toprağa oturttu."}, {"fa": "وعده قتلم نمی یابد وفایی زان پری\nاین تغافل می کشد امروز یا فردا مرا", "tr": "O peri yüzlü beni öldüreceğini vadetmişti. Sözünü yerine getirmiyor. Onun bu aldırmazlığı beni ya bugün ya yarın muhakkak öldürür."}, {"fa": "یار شمع مجلس هر بی سر و پا می شود\nچون نسوزد آتش غیرت ز سر تا پا مرا", "tr": "Sevgili her olur olmaz adamın meclisini aydınlatıyor (meclisin mumu oluyor). Haset ateşi beni nasıl baştan aşağı yakmaz."}, {"fa": "شمع هم می گرید از بی همنشینی شام غم\nنی همین کشتست درد بی کسی تنها مرا", "tr": "Mum dahi gam gecesinde bir yâri, bir dostu olmadığı için ağlıyor. Bu kimsesizlik derdi yalnız beni öldürmemiştir (söndürmemiştir)."}, {"fa": "ترک ذوق عاشقی کردم فضولی بعد ازین\nنی مرا ذوقیست دنیا را نه از دنیا مرا", "tr": "Fuzuli, âşıklık zevkini terk ettim. Bundan sonra ne dünya benden bir şey anlar ne de ben dünyadan bir zevk duyarım."}, {"fa": "دل ز من مستان نمی خواهم که غم باشد ترا\nبا وجود لطف یار دل ستم باشد ترا", "tr": "Gönlümü benden alma; istemem ki sana bir zahmet versin. Her ne kadar o maddeden muarra, latif bir şey ise de gene sana gönül yüklemek bir zulümdür."}, {"fa": "کیست یوسف تا ترا مانند باشد در جمال\nاو مگر از جمله خیل و حشم باشد ترا", "tr": "Yusuf kimdir ki güzellikte sana benzesin? Belki o senin hizmetkârların, maiyetin arasında bulunabilir."}, {"fa": "نیست طبع نازکت را تاب شرح درد دل\nکی کنم کاری کزان بیم الم باشد ترا", "tr": "Mizacın o kadar naziktir ki derdimi sana açsam tahammül edemezsin. Seni üzmek ihtimali olan bir şeyi hiç yapar mıyım?"}, {"fa": "بیش ازین مپسند در دام بلا زارم بکش\nگر نباشد عاشق زاری چه کم باشد ترا", "tr": "Beni bela tuzağında bu kadar inletme; öldür. Bir inleyen âşığın eksilirse bir şeyin eksilir mi?"}, {"fa": "چون حباب می فلک تا چشم بر هم می زنی\nمی زند بر هم گر استقبال جم باشد ترا", "tr": "Cemşid kadar ikbale sahip olsan gene felek, şarap üzerindeki su kabarcığı gibi göz açıp kapayıncaya kadar seni mahveder."}, {"fa": "ترک عالم کن که در عالم نمی ارزد بغم\nگر هزاران گنج بر بالای هم باشد ترا", "tr": "Âlemi terk et. Birbiri üzerine yığılmış binlerce hazineye sahip dahi olsan çekeceğin ıstıraba değmez."}, {"fa": "پا منه از حد خود بیرون که هر جا پا نهی\nاز ره رفعت فلک خاک قدم باشد ترا", "tr": "Haddini bil. Ondan ileri ayak atma. Böyle yaparsan öyle yükselirsin ki felek sana hâk-i pây olur."}, {"fa": "با فغان و ناله آزردی فضولی خلق را\nجا همان به بر سر کوی عدم باشد ترا", "tr": "Fuzuli, o kadar feryad ü figan ettin ki halk artık muztarip oldu. İyisi mi senin yerin adem diyarı olmalıdır."}, {"fa": "شبی آمد به خوابم یار و برد از دیده خوابم را\nسبب آن خواب شد بیداری چشم پر آبم را", "tr": "Sevgilim, bir gece rüyama (hab) girdi. Uykuyu gözümden aldı gitti. Bu yaşlı gözümün uykusuzluğuna sebep o rüya (hab) oldu."}, {"fa": "ز باد تند ناصح موج دریا بیش می گردد\nچه سود از کثرت پندت دل پر اضطرابم را", "tr": "Ey bana öğüt veren, rüzgâr sertleştikçe dalgalar daha fazlalaşır. Bu muztarip (mevc, ıztırap) gönlüme o kadar çok nasihatin ne faydası var ki."}, {"fa": "بتی دیدم روان شد خون دل از دیده ام هر سو\nفلک در بزم غم بر سنگ زد جام شرابم را", "tr": "Bir güzel (put) gördüm. Gönlümün kanı gözlerimden fışkırdı. Felek, gam bezminde şarap kadehimi taşa çaldı."}, {"fa": "درون دل به تیغ شوق شد پر کاله پر کاله\nچه داند چیست مضمون هر که نگشاید کتابم را", "tr": "Sevgi kılıcı ile gönlümün içi parça parça oldu. Kitabımı açmayan onun manasını anlayabilir mi?"}, {"fa": "نمی خواهم که از خوبان شکایت بر زبان رانم\nهمان بهتر نپرسد هیچ کس حال خرابم را", "tr": "Güzellerden şikâyet yolunda bir tek söz söylemek istemem. İyisi mi kimse benim harap hâlimi sormasın."}, {"fa": "چو تاری گشته ام از ضعف و ضعفم پیش می گردد\nفلک هر چند می گرداند افزون پیچ و تابم را", "tr": "O kadar zaifim ki ipliğe döndüm. Felek beni fazla büktükçe (piç ü tâb) za‘fım fazlalaşıyor."}, {"fa": "فضولی نیست امکان وفا در مردم عالم\nمدان بیهوده زین جمع پریشان اجتنابم را", "tr": "Fuzuli, el oğlunda vefa denen şeyden eser kalmamış. Bu perişan cemiyetten beyhude yere çekinmiyorum."}, {"fa": "بستی گره از بهر جفا زلف دو تا را\nبرداشتی از روی زمین رسم وفا را", "tr": "Bana cefa olsun diye kıvırcık saçlarını ördün. Vefakârlık usulünü yeryüzünden kaldırdın."}, {"fa": "تا بسته مژگان تو گشتیم بغمزه\nزد چشم تو بر هم همه جمعیت ما را", "tr": "Yan bakışının güzelliği bizi kirpiklerine esir ettiğinden beri gözün gönlümüzde huzur ve rahat bırakmadı."}, {"fa": "کس نیست که آیین جفا به ز تو داند\nآیا ز که آموختی آیین جفا را", "tr": "Cefa etmek usulünü senden daha iyi bilen kimse yoktur. Bu cefa usulünü acaba kimden öğrendin?"}, {"fa": "از دایره چرخ کشیدم سر همت\nتا چند کشم منت هر بی سر و پا را", "tr": "Dünya zevklerinden, alâkalarından sıyrıldım. Himmet başımı bu çarhın dairesinden kurtarıp yükseldim. Ne zamana kadar her aciz (bî-serüpa: çarh)in minnetini çekeceğim."}, {"fa": "عمریست براهت شده ام خاک که گاهی\nآیی سوی من بوسه زنم آن کف پا را", "tr": "Bir ömürdür ki senin yolunda toprak olmuşum. Belki bir gün benim tarafıma gelirsin. Ayağını öpmek saadetine erişirim."}, {"fa": "هر لحظه بمن می رسد از چرخ بلایی\nدامیست قد خم شده ام مرغ بلا را", "tr": "Her lahza bana çarhtan bir bela geliyor. Bükülmüş belim bela kuşuna bir tuzak oldu."}, {"fa": "گر قصد دل و دین فضولی کند آن بت\nناصح مده آزار مکن منع خدا را", "tr": "Eğer o güzel (put) Fuzuli’nin gönlüne, dinine kasd ederse, ey nasih (öğüt veren), Allah aşkına onu incitme, onu menetme!"}, {"fa": "نی دل و دین ماند نه صبر و شکیبایی مرا\nرفته رفته جمع شد اسباب تنهایی مرا", "tr": "Bende ne gönül, ne din, ne sabır kaldı. Yavaş yavaş yalnız kalmam için lâzım olan şeyler toplandı."}, {"fa": "چند بر من رو نهد هر جا که باشد محنتی\nدل گرفت از صحبت یاران هر جایی مرا", "tr": "Ne zamana kadar nerede bir mihnet varsa bana yönelecek. Bu hercai dostların sohbetinden artık sıkılmaya başladım."}, {"fa": "گر نیندازم نظر بر عارضت از صبر نیست\nرشک می آید بدیدارت ز بینایی مرا", "tr": "Eğer yüzüne bakmıyorsam bu sabr ü tahammülden değildir. Senin didarın karşısında görebilmek kabiliyetimi kıskanıyorum."}, {"fa": "گر بمیرم با کسی هرگز نگویم درد دل\nدرد پنهانست بی شک به ز رسوایی مرا", "tr": "Ölsem dahi kimseye gönlümün derdini söylemem. Şüphesiz gizli dert benim için rüsvâlıktan daha iyidir."}, {"fa": "کرد مستغنی ز فرش خاک و چتر آسمان\nدر بساط شوق ذوق بی سر و پایی مرا", "tr": "Muhabbet yaygısı üzerinde aciz ve miskin bir hâlde oturmak bana öyle bir zevk veriyor ki artık toprak yaygısından, gök çadırından müstağniyim."}, {"fa": "سر نمی پیچم ز فرمان تو ای سلطان عشق\nبنده فرمان پذیرم هر چه فرمایی مرا", "tr": "Ey aşk sultanı, senin emrinden bir an harice çıkmam. Her ne emredersen emir kuluyum."}, {"fa": "قصه فرهاد و مجنون را فضولی کس نخواند\nتا بر آمد نام در عالم به شیدایی مرا", "tr": "Fuzuli, âleme mecnun bir âşık olarak ün saldığın andan beri Ferhat ve Mecnun’un hikâyelerini kimse okumuyor."}, {"fa": "من به غم خو کرده‌ام جز غم نمی‌باید مرا\nور ز غم ذوقی رسد آن هم نمی‌باید مرا", "tr": "Ben kedere alışmışım. Ondan başka bir şeyin bana lüzumu yok. Eğer keder bana zevk verecekse onu dahi istemem."}, {"fa": "گر گریزانم ز خود در دشت عزلت دور نیست\nوحشیم جنس بنی‌آدم نمی‌باید مرا", "tr": "Uzlet çölünde eğer kendi kendimden dahi kaçıyorsam hayret etmeyin. Ben vahşiyim. Âdem oğullarının bana lüzumu yok."}, {"fa": "کس نمی‌خواهم که بینم گر همه چشم من است\nاختلاط مردم عالم نمی‌باید مرا", "tr": "Bana gözüm kadar aziz olsa dahi kimseyi görmek istemiyorum. Dünya halkı (merdüm) ile ülfet ve ünsiyetin bana lüzumu yok."}, {"fa": "ساقیا چون می دهی بخش مرا بر خاک ریز\nمی نمی‌نوشم دل خرم نمی‌باید مرا", "tr": "Ey sâkî, şarap sunduğun zaman benim hissemi yere dök. Ben şarap içmiyorum. Çünkü bahtiyar gönlün bana lüzumu yok."}, {"fa": "می‌دهد رخت نشاطم را به سیلاب سرشک\nبی‌جمالت دیده پر نم نمی‌باید مرا", "tr": "Senin güzelliğini temaşa etmedikten sonra bu yaşlı gönlün bana lüzumu yok. Çünkü bütün sevincimi (sevincimin mal ü mülkünü) gözyaşı…"}, {"fa": "با سفالی قانعم پر درد در کوی مغان\nمسند جمشید و جام جم نمی‌باید مرا", "tr": "Muğların diyarında (meyhanede) bir toprak çanağa razıyım. Bana Cemşid’in ne saltanatı lâzımdır, ne de kadehi."}, {"fa": "با جفای او فضولی از وفا مستغنیم\nبا جراحت خوشدلم مرهم نمی‌باید مرا", "tr": "Fuzuli, onun cefası olduktan sonra vefadan müstağniyim. Ben yaramdan memnunum. Merhemin lüzumu yok."}, {"fa": "نم نماند از تاب خورشید رخت در خاک ما\nچون نگرید چون بگرید دیده نمناک ما", "tr": "Yanağının güneşi o derece hararetli ki toprağımızda rütubetten eser kalmadı. Islak gözümüz nasıl ağlamasın, nasıl ağlasın."}, {"fa": "تا ز سوز سینه ما گشت پیکان تو آب\nشست گرد غیر را از صفحه ادراک ما", "tr": "Senin okunun temreni sinemizin hararetinde eriyeli beri idrakimizin sahifesinden ağyar tozunu sildi, yıkadı."}, {"fa": "عاشقی باید چو بت از سنگ و بی باک از جفا\nتا کند جوری بکام دل بت بی باک ما", "tr": "Put gibi taştan ve cefaya dayanıklı bir âşık lâzımdır ki bizim pervasız sevgilimiz (putumuz) istediği gibi ona cevr ü cefa etsin."}, {"fa": "رام شد شمعی که چون آتش سر از ما می کشد\nکرد آخر کار خود تأثیر عشق پاک ما", "tr": "O ateş gibi bize kafa tutan (serkeş) parlak (şem‘) sevgili nihayet bize ram oldu. Temiz aşkımız nihayet tesirini böyle gösterdi."}, {"fa": "دل بلای جان بی خود گشت و جسم بی قرار\nآتشی افکند عشقت در خس و خاشاک ما", "tr": "Gönül, kendinden geçen can ile kararsız, muztarip cismin başına bela oldu. Senin sevgin bizim çörçöpümüzü ateşe verdi."}, {"fa": "گشت دل صد پاره و بهر تماشای رخت\nکرد هر سو سر برون از سینه صد چاک ما", "tr": "Gönül yüz parça oldu ve senin güzelliğini temaşa için her biri şerha şerha (yüz yerinden yarılan) sinemizden fırladı, çıktı."}, {"fa": "گفتمش از خود فضولی را میفکن دور گفت\nنیست این صید محقر قابل فتراک ما", "tr": "Dedim ki: Fuzuli’yi böyle yanından uzaklaştırma. Şöyle cevap verdi: Bu değersiz av, bizim atımızın terkisine asılmağa layık değildir."}, {"fa": "گر سر کویت شود مدفن پس از مردن مرا\nکی عذاب قبر پیش آید دران مدفن مرا", "tr": "Öldükten sonra senin diyarına gömülsem hiç kabir azabı duyar mıyım?"}, {"fa": "چند باشم در جدل با خود ز غم ساقی بیار\nشیشه می تا رهاند ساعتی از من مرا", "tr": "Sâkî, bu gam yüzünden daha ne kadar kendi kendimle mücadele edeceğim. Bir şişe şarap getir. Hiç olmazsa bir saat beni benden kurtarsın."}, {"fa": "دوست چون می خواهدم رسوا ندارم چاره ای\nمی شوم رسوا چه باک از طعنه دشمن مرا", "tr": "Dost benim rüsvâ olmamı istedikten sonra ne çare, olurum. Düşman bana ta‘n edecekmiş, etsin; niye korkayım."}, {"fa": "ز آتش دل چون نمی سوزد روان گویا که هست\nاستخوانهای بدن فانوس وش زاهن مرا", "tr": "Gönlümün ateşinden ruhum nasıl yanmıyor. Sanki bedenimdeki kemikler fanus gibi demirden yapılmış."}, {"fa": "کام من معنیست نی صورت ز یوسف طلعتان\nمن نه یعقوبم چه ذوق از بوی پیراهن مرا", "tr": "Yusuf gibi güzellerden benim muradım ma‘nadır; suret değil. Ben Yakup değilim. Gömlek kokusundan ne zevk duyarım."}, {"fa": "خنده دارند بی پروا ز آسیب خزان\nچون نیاید گریه بر گلهای این گلشن مرا", "tr": "Bu gülşenin gülleri sonbahar felaketini düşünmüyorlar; gülüp açılıyorlar. Nasıl bunların hâline ağlanmaz."}, {"fa": "رفت جان از تن برون تن شد فضولی خاک ره\nعشق او شد آفت جان و بلای تن مرا", "tr": "Fuzuli, can tenden çıktı. Ten toprak oldu. Onun aşkı canıma âfet, tenime bela oldu."}, {"fa": "به دل از گلعذاری خارخاری کرده‌ام پیدا\nبحمدالله نیم بیکار کاری کرده‌ام پیدا", "tr": "Bir gül yanaklının aşkı ile gönlümü paralayıp duruyorum (harî harî). Hamdolsun işsiz değilim. Kendime bir iş bulmuşum."}, {"fa": "درین گلشن چو گلبن از جفای گردش گردون\nبسی خون خورده‌ام تا گلعذاری کرده‌ام پیدا", "tr": "Bir gül yanaklı dilber elde edinceye kadar bu gülşende dünya cefasından gül fidanı gibi çok kan yutmuşum."}, {"fa": "به خون دیده و دل کرده‌ام صید سگ کویش\nسگ صیدم درین صحرا شکاری کرده‌ام پیدا", "tr": "Onun diyarının köpeğini göz ve gönül kanı ile avlamışım. Av köpeğiyim; bu sahrada bir av elde etmişim."}, {"fa": "به گرداب سرشک افتاده‌ام در دور گیسویش\nچه باک از فتنه دوران حصاری کرده‌ام پیدا", "tr": "Onun saçının devrinde gözyaşı girdabına düşmüşüm. Devranın fitnesinden ne korkum var; kendime bir kâle edinmişim."}, {"fa": "به من بسپرده پنهان گلرخان نقد غم خود را\nمیان گلرخان خوش اعتباری کرده‌ام پیدا", "tr": "Gül yanaklı güzeller aşk ıztırabının nakdini gizlice bana emanet etmişlerdir. Gül yanaklılar arasında itibarım yerindedir."}, {"fa": "خیالت همدم و همراز من بس روز تنهایی\nز تو مستغنیم غیر از تو یاری کرده‌ام پیدا", "tr": "Kimsesiz günlerimde bana arkadaş ve sırdaş senin hayalindir. Artık sana muhtaç değilim; başka bir sevgili elde ettim."}, {"fa": "فضولی درد دل با سایه می‌گویم نیم بی‌کس\nبحمدالله که چون خود خاکساری کرده‌ام پیدا", "tr": "Fuzuli, gönlümün derdini gölgemle söylüyorum. Artık kimsesiz değilim. Allah’a çok şükür, kendim gibi bir hakir (hâksâr) buldum."}, {"fa": "تا بوده ایم همدم غم بوده ایم ما\nغم را ملازم همه دم بوده ایم ما", "tr": "Biz olduk bittik gam ile yoldaşız. Daima o bizimle beraberdir."}, {"fa": "غم را ز من نبوده جدایی مرا ز غم\nهر جا که بوده ایم بهم بوده ایم ما", "tr": "Ne gam benden ayrıldı, ne ben ondan; nerede olsak beraberiz."}, {"fa": "پیش از وجود با غم لعل تو عمرها\nهمراز تنگنای عدم بوده ایم ما", "tr": "Daha var olmadan evvel dudağına karşı duyduğumuz sevginin ıstırabı ile yokluk zindanının sırdaşı olarak ne uzun seneler yaşadık."}, {"fa": "تا بر کمان ابروی تو بسته ایم دل\nدایم نشان تیر ستم بوده ایم ما", "tr": "Senin kaşının yayına gönül bağlayalı beri daima zulüm okuna hedef olmuşuzdur."}, {"fa": "هرگز نگشته است کم از ما بلای تو\nیک لحظه بی بلای تو کم بوده ایم ما", "tr": "Gönlümüzde senin sevginin elemi asla eksilmemiştir. Senin belanın bir an bizden ayrıldığı pek az vakidir."}, {"fa": "هر جا نهاده ایم قدم در ره نیاز\nافتاده تر ز خاک قدم بوده ایم ما", "tr": "Niyaz yolunda her yere ayağımızı koymuşuzdur. Ayak toprağından daha hakir olduk."}, {"fa": "یکدم نبوده ایم فضولی بکام دل\nپیوسته مبتلای الم بوده ایم ما", "tr": "Fuzûlî, bir an gönlümüzün istediği gibi yaşayamadık; ömrümüz daima elem içinde geçti."}, {"fa": "نشان تیر آهم گشته‌ای ای آسمان شب‌ها\nترا بر سینه پیکان‌هاست هرسو نیست کوکب‌ها", "tr": "Ey gökyüzü, geceleri sen âhımın okuna hedef oldun. Göğsünde görünenler yıldız değil, ok temrenleridir."}, {"fa": "دل بی‌خود درون سینه دارد فکر زلفینت\nبسان مرده‌ای کش مونس قبرند عقرب‌ها", "tr": "Kendinden geçen gönlün sinesinin içinde yanağının iki yanını süsleyen zülüflerin hasreti vardır. O, kabrinde akreplerle haşır neşir olan bir ölüye benziyor."}, {"fa": "خطست آن یا برآمد دود دل از بس که محبوبان\nزدند آتش به دل‌ها در زنخدان‌ها و غبغب‌ها", "tr": "Yüzündeki ayva tüyleri midir, yoksa güzellerin çene çukurlarıyla gerdanlarından tutuşan gönüllerin dumanı mıdır?"}, {"fa": "جفا را از معلم یاد می‌گیرند محبوبان\nز مکتب‌هاست فریادم که ویران باد مکتب‌ها", "tr": "Güzeller cefayı mektepte hocalarından öğreniyorlar. Feryadım mekteplerdendir. Viran olsun bu mektepler!"}, {"fa": "ز خاک رهگذر هر ذره‌ای را شهسواری دان\nکه بر دل داغ‌ها دارد ز نقش نعل مرکب‌ها", "tr": "Yoldan geçerken gördüğün her zerre bil ki gönlünde atların nal izlerinden dağlar (yaralar) taşıyan bir şehsüvardır."}, {"fa": "فکندی عکس در می گشت رشکم زآنکه می‌ترسم\nنهی لب بر لب ساغر رسانی بر لبت لب‌ها", "tr": "Şaraba aksini düşürdün, kıskanıyorum. Korkuyorum ki dudağını kadehin dudağına değdireceksin de dudaklar dudağının visaline erecek."}, {"fa": "چه شد یارب که در شب‌های تنهایی نمی‌یابد\nفضولی کام دل هرچند می‌خواهد به یارب‌ها", "tr": "Yarabbi, ne oldu ki Fuzulî kimsesiz gecelerinde “yarab, yarab” diye yalvardığı hâlde bir türlü muradına eremiyor."}, {"fa": "نه از عارست گر آن مه نیارد بر زبان ما را\nچه گوید چون بپرسد نیست چون نام و نشان ما را", "tr": "O ay gibi güzel sevgili adımızı anmıyorsa bu utancından değildir. Bizim nam u nişanımız yok ki ne söylesin, nasıl sorsun?"}, {"fa": "فلک چنگیست خم ما ناتوان‌ها تارهای او\nرضای دوست مضرابی که دارد در فغان ما را", "tr": "Felek iki büklüm bir çenktir. Biz takatsizler de onun telleri. Sevgilinin rızası da bizi daima inleten bir mızraptır."}, {"fa": "با فغان ظاهرم وز ضعف پنهان وه که سودایت\nبه بی نام و نشانی کرد رسوای جهان ما را", "tr": "Ancak feryat edince meydana çıkıyorum. O kadar zaifim ki halk beni görmüyor. Ne yazık ki senin aşkın bizi âleme bir nam u nişan olarak meşhur etti."}, {"fa": "چه می پرسی ز احوال درون در آتش عشقت\nسیه ماریست مغز سوخته در استخوان ما را", "tr": "Gönlümüzün (içimizin) hâlini ne soruyorsun. Nasıl olacak; senin aşkının ateşinden kemiğimizin içindeki yanmış ilik siyah bir yılana benziyor."}, {"fa": "طبیبا در علاج درد دل ماهر شدی اما\nچه حاصل زانکه گشتی از خطا در امتحان ما را", "tr": "Ey tabib, gönül derdini tedavide maharet kazandın ama ne faide, tecrübe (imtihan) sırasında yaptığın hata ile bizi öldürdün."}, {"fa": "ز خاک آستانش روی ما مشکل که برگردد\nاگر مانند اختر سر رسد بر آسمان ما را", "tr": "Eğer yıldız gibi başımız göklere erse gene onun eşiğinin toprağından yüz çevirmemiz pek müşküldür."}, {"fa": "فضولی هست نقد جان و تن نذر بتان ما را\nکه گرداند خدا شرمنده روی بتان ما را", "tr": "Fuzuli, can ve ten nakdi güzellere (putlara) nezredilmiştir. Allah elbette o güzeller (putlar) karşısında bizi utandırmaz."}, {"fa": "از آن رو دوست می‌دارم خط رخسار خوبان را\nکه بهر الفت ایشان سبب دانسته‌ام آن را", "tr": "Güzellerin yanağındaki ayva tüylerini onlarla görüşmeme bir vesile telakki ederim de onun için severim."}, {"fa": "جفاها می‌کشیدم بندهٔ آن خط مشکینم\nکه بر من کرد ظاهر صدهزاران لطف پنهان را", "tr": "Çok cefalar çekiyordum; fakat o misk kokulu ayva tüylerine köle olayım. Zira bana yüz binlerce gizli lütuflar izhar etti."}, {"fa": "کنون دل می‌تواند کرد سِیْرِ باغ رخسارش\nکه پوشید آن خط مشکین سر چاه زنخدان را", "tr": "Şimdi gönül onun yanağının bağında dolaşabilir. Zira o misk kokulu ayva tüyleri çene çukuru kuyusunun üzerini örttü."}, {"fa": "از آن خط معنبر هر سر مویی زبانی شد\nصلای خوان وصلش داد دل‌های پریشان را", "tr": "O anber kokulu ayva tüylerinin her tüyü bir dil oldu. Perişan gönülleri onun visalinin sofrasına çağırıyor."}, {"fa": "نمی‌سوزد دلم را با جفا تا گرد خط پیدا\nشب آمد کرد زایل گرمی خورشید رخشان را", "tr": "Yüzünde ayva tüyleri belirdiğinden beri artık gönlümü cefa ateşi ile yakmıyor. Gece olunca parlak güneşin harareti zail oldu."}, {"fa": "ز خط مصحف رخسار او ای دل مشو غافل\nگر این آیت مسلمان ساخته آن نامسلمان را", "tr": "Onun yanağının mushafındaki yazıdan (ayva tüylerinden), ey gönül, gafil olma. Zira bu ayet o kâfiri Müslüman etti."}, {"fa": "فضولی نیست غیر خط رخسار پری‌رویان\nطلسمی کآشنا با هر پری می‌سازد انسان را", "tr": "Ey Fuzuli, insanı her bir peri ile dost eden tılsım ancak peri yüzlülerin yanaklarında beliren ayva tüyleridir."}, {"fa": "ای آنکه آفت دل و جان و تنی مرا\nمن دوستم ترا تو چرا دشمنی مرا", "tr": "Ey gönlümün, canımın, tenimin âfeti olan güzel, ben sana dostum. Sen niçin bana düşmansın."}, {"fa": "ای جان چه شود زانکه کنم میل زیستنی\nچون مهربان نه تو که جان منی مرا", "tr": "Benim canım olan sen bana yâr olmadıktan sonra, haydi yaşamak isteyeyim, ne faide?"}, {"fa": "یوسف قرار قیمت خویش از زمانه یافت\nای در بی بها تو از او احسنی مرا", "tr": "Yusuf, kıymetinin derecesini zamanında buldu, gördü. Ey baha biçilemeyecek kadar değerli inci, sen bana ondan güzelsin."}, {"fa": "شمعم من آتشی تو ز تو دوریم مباد\nزیرا حیات بخش دل روشنی مرا", "tr": "Ben bir mumum, sen de ateşsin. Allah beni senden ayırmasın. Zira parlak gönlüme hayat veren sensin."}, {"fa": "مانند شمع سوخته حسرت توام\nبا آنکه صبح وش سبب مردنی مرا", "tr": "Sen sabah gibi benim ölmeme (sönmeme) sebep olduğun hâlde gene senin hasretinle mum gibi yanıyorum."}, {"fa": "من لاله بهار غمم شبنمم تویی\nای گوهر سرشک که در دامنی مرا", "tr": "Ben gam baharının gelinciğiyim. Ey eteğime düşen gözyaşı incisi, sen de benim üzerimde çiğ danesin."}, {"fa": "در عشق جز تو نیست فضولی حسود من\nمعلوم می شود که شریک فنی مرا", "tr": "Ey Fuzuli, aşk hususunda senden başka bana haset eden yok. Anlaşılıyor ki bu fende bana ancak sen rekabet ediyorsun."}, {"fa": "گلرخا، نوش لبا، سیم برا، سرو قدا\nما بدا قبلک ما فیک من الحسن بدا", "tr": "Ey gül yanaklı, tatlı dudaklı, gümüş göğüslü, servi boylu, sende görünen güzellikler senden evvel kimsede görülmemişti."}, {"fa": "من نه اینم که دهم غیر ترا در دل ره\nاکره الشرک فلا اشرک ربی احدا", "tr": "Ben gönlünde senden başkasına yer verecek adam değilim. Şirkten ikrah ederim. Allah’a kimseyi şerik tutmam."}, {"fa": "در راه عشق بتان بود تردد دشوار\nکیف لا احمد من سهل امری و هدا", "tr": "Güzellerin (putların) aşkı yolunda dolaşmak güçtür. İşimi kolaylaştıran, bana doğru yolu gösterene nasıl hamd ü şükretmem."}, {"fa": "ذوق عشقت که ز روز ازلم همره بود\nطاب لی یجعله الله رفیقی ابدا", "tr": "Aşkının zevki ezel gününden beri bana yoldaştır. Ben onu çok seviyorum. Allah beni ebediyen onun yoldaşı etsin."}, {"fa": "گم شدم در طلب کعبه مقصود ای خضر\nبمن گم شده راهی بنما بهر خدا", "tr": "Ey Hızır, maksud kâbem’i ararken yolumu kaybettim. Allah aşkına, ben yolunu şaşırmışa bir yol göster."}, {"fa": "نقد جان نیست روا صرف شود بی وجهی\nبنما بهر خدا روی که سازیم فدا", "tr": "Can nakdini sebepsiz (vecihsiz) harcamak reva değildir. Allah aşkına yüzünü göster ki onu feda edelim."}, {"fa": "چند پرسی که چه شد حال فضولی بی من\nچه شود حال کسی کز تو فتادست جدا", "tr": "Bensiz Fuzuli ne hallere girdi diye ne sorup duruyorsun? Senden ayrı düşenin hâli ne olur ki?.."}, {"fa": "گر نباشد قید آن گیسوی خم بر خم مرا\nکی بصد زنجیر بتوان داشت در عالم مرا", "tr": "Eğer o kıvrım kıvrım saçlar olmasaydı acaba yüzlerce zincir beni bu âleme bağlayabilir miydi."}, {"fa": "با خیال آن پری خو کرده ام ناصح برو\nخوش نمی آید ملاقات بنی آدم مرا", "tr": "Ben o perinin hayaline alışmışım. Ey bana öğüt veren, işine git. Âdem oğlu ile karşılaşmak hoşuma gitmiyor."}, {"fa": "نه منم بی غم نه غم بی من دمی ایزد مگر\nآفرید از بهر من غم را و بهر غم مرا", "tr": "Ne ben bir an gamsız olurum, ne de gam bensiz. Galiba Allah gamı benim için, beni de gam için yaratmış."}, {"fa": "بی لب میگون آن گلرخ نمی یابم فرح\nگر شود جمشید ساقی می ز جام جم مرا", "tr": "O gül yanaklının şarap renkli dudağı olmazsa gönlüm açılmıyor. İsterse sâkî, Cemşid kadehi de (câm-ı Cem) olsun."}, {"fa": "گرچه دارم جسمی از سودای زلفت ناتوان\nمن هلال اوج سودایم نه بینی کم مرا", "tr": "Gerçi vücudum saçının sevdasıyla zayıf ve kudretsizdir; fakat ben sevda göğünün en yüksek yerinde parlayan bir hilâlim. Beni hakir (eksik) görmezsin."}, {"fa": "کو ستمکاری که از غم بر دلم داغی نهد\nدل گرفت ای همنشین از خاطر خرم مرا", "tr": "Yüreğimi gamla dağlayan bir zalim yok mu? Ey dostum, sevinç ve neş’eden canım sıkıldı."}, {"fa": "ناله دارد فضولی درد سر می آورد\nروز تنهایی نمی خواهم شود همدم مرا", "tr": "Yalnız günümde arkadaş istemem. Zira Fuzuli’nin feryadı onu taciz eder."}, {"fa": "هیچ‌گه بر حال من رحمی نمی‌آید ترا\nمی‌کشی ما را مگر عاشق نمی‌باید ترا", "tr": "Hâlime hiç acımıyorsun, bizi öldürüyorsun. Sana âşık lâzım değil mi?"}, {"fa": "می‌شود آتش ز باد افزون چه باشد گر مدام\nحسن روز افزون ز آه من بیفزاید ترا", "tr": "Rüzgâr ateşi coşturur. Benim de âhım daima senin günden güne artan güzelliğini artırıyorsa buna hayret etme."}, {"fa": "گر ز من در خاطر پاکیزه داری اضطراب\nمن شوم آواره تا خاطر بیاساید ترا", "tr": "Temiz kalbin benim yüzümden üzülüyorsa ben başımı alıp gideyim. Tek senin gönlün rahat olsun."}, {"fa": "چرخ می‌داند که در من تاب دیدار تو نیست\nزین سبب هرگز نمی‌خواهد که بنماید ترا", "tr": "Felek biliyor ki ben senin güzelliğin karşısında mahvolurum, ona tahammül edemem; onun için seni asla bana göstermek istemiyor."}, {"fa": "بسته خود را به آن شاخ گل ای دل غنچه‌وار\nتا نکردی دور ازو آن به که نگشاید ترا", "tr": "Ey gönül, gonca gibi kendini o gül dalına bağlamışsın. Seni açmasın daha iyi; ta ki ondan ayrılmayasın."}, {"fa": "در جفا و در وفا ای مه نداری اختیار\nنشئهٔ حُسن است حاکم تا چه فرماید ترا", "tr": "Ay yüzlü sevgilim, âşıka cefa da etsen, vefa da göstersen bunlar senin iradenin haricindedir. Hâkim olan güzellik neş’esidir. Sen ancak onun emrettiklerini yapıyorsun."}, {"fa": "می‌نهی سر بر ره آن مه فضولی دم به دم\nزین شرف شاید که سر بر آسمان ساید ترا", "tr": "Fuzuli, her an o ay yüzlü sevgilinin yoluna baş koyuyorsun. Bu şeref ile başın göklere erse layıktır."}, {"fa": "چون شمع سوخت آتش محنت تن مرا\nغم پاره پاره ساخت دل روشن مرا", "tr": "Mihnet ateşi vücudumu mum gibi yaktı. Gam ârif gönlümü parça parça etti."}, {"fa": "بر من بسوخت در غم عشقت دل رقیب\nشادم که غم بسوخت دل دشمن مرا", "tr": "Senin aşkının ıstırabı içinde nasıl yandığımı gördü de rakibin yüreği yandı. Düşmanımın gönlü gam içinde yandığı için o kadar seviniyorum ki…"}, {"fa": "واجب شد اجتناب من از ماه پیکران\nچون فرض کرده اند بخود کشتن مرا", "tr": "Beni öldürmeyi kendilerine farz bildikleri için ay tenli güzellerden çekinmek bana vacip oldu."}, {"fa": "مردم بداغ لاله رخان گریهای ابر\nخواهم که لاله زار کند مدفن مرا", "tr": "Her an lâle yanaklıların yüreğine açtığı yaralarıyla ağlayan bulutun gözyaşları isterim ki benim mezarımı gelincik tarlasına çevirsin."}, {"fa": "سویم نمی کند الم بی کسی گذر\nتا غم شناخت است ره مسکن مرا", "tr": "Gam evimin yolunu öğreneli beri yalnızlık elemi bana hiç uğramıyor."}, {"fa": "عمریست کز لباس تعلق مجردم\nنگرفته است دست غمی دامن مرا", "tr": "Bir ömürdür ki taalluk (dünyaya bağlanma) elbisesinden soyunmuşum. Onun için hiçbir gam eli eteğimi tutmuyor."}, {"fa": "از غم مرا نماند فضولی ره گریز\nبگرفت سیل تفرقه پیرامن مرا", "tr": "Perişanlık seli etrafımı çevirdi. Fuzuli, gamdan kurtuluş yolu kalmadı."}, {"fa": "این که در سر هوس آن قد رعناست مرا\nفیض خاصیست که از عالم بالاست مرا", "tr": "Başımda o güzel endamın sevgisi, âlemi beladan bana hususi bir feyizdir."}, {"fa": "اثر نور الهیست که در دل دارم\nاین که پیوسته نظر بر رخ زیباست مرا", "tr": "Daima güzel yüzleri temaşa etmem gönlümdeki ilâhî nurun eseridir. Aşk yüzünden böyle rüsvâ olmayı ben kendim istemiş değilim. Bu hayatı vücuda getiren kudret böyle olmamı istemiş."}, {"fa": "بخود از عشق نه من خواسته ام رسوایی\nآنکه این جنبش ازو خاست چنین خواست مرا", "tr": "Âşıklığımın menşe’i bu âlemin mahsulü değildir. Bir âlem vardır ki bana bu neş’e oradan geliyor."}, {"fa": "نشأه عاشقیم حاصل این عالم نیست\nعالمی هست که این نشاه از آنجاست مرا", "tr": "Ben zünnarı belime bugün bağlamış değilim. Ezelden beri deli gönlümde güzellerin (putların) sevgisi vardır."}, {"fa": "من میان بسته زنار نه امروز شدم\nز ازل شوق بتان در دل شیداست مرا", "tr": "Hilkat beni niçin bu renk ile süsledi (bu şekilde vücuda getirdi) diye hayretten gönül kanına gark oldum."}, {"fa": "غرق خونابه دل کرد مرا این حیرت\nکه چرا صنع بدین رنگ بیار است مرا", "tr": "Fuzuli, kaza niçin beni böyle beli bükülmüş yarattı (rast kerd-tanzim etti) diye o kadar düşünüyorum ki bu düşüncenin yükü endamımı yay’a çevirdi (belimi büktü). Not: Bu gazelde OCR’da beyit sayısı eksik girişik görünüyor; metinde görünen kısmı imlâen düzelttim."}, {"fa": "باز این فکر فضولی قد من کرد کمان\nکه چرا کرد قضا با قد خم راست مرا", "tr": "[Tercüme Eksik]"}, {"fa": "چشم بگشادم ببالایت بلا دیدم ترا\nبیخودم کردی نمی دانم کجا دیدم ترا", "tr": "Yüksek boyunu, bosunu (bîlâ) görür görmez anladım ki sen bir felaketsin (bela). Aklımı başımdan aldın. Bilmiyorum seni nerede gördüm."}, {"fa": "از تو در طفلی جفا می دیدم اما اندکی\nدر جوانی محض بیداد و جفا دیدم ترا", "tr": "Çocukluğunda da bana cefa ediyordun amma bu kadar değildi. Gençlik çağında cefa ve zulmün ta kendisi oldun."}, {"fa": "بی وفایی را نه امروز از کسی آموختی\nماه من روزی که دیدم بی وفا دیدم ترا", "tr": "Vefasızlığı bir başkasından bugün öğrenmiş değilsin. Ay yüzlü sevgilim, seni gördüğüm günden beri vefasızsın."}, {"fa": "کاشکی هرگز نمی کردم گذر سوی درت\nدوش با بیگانه چند آشنا دیدم ترا", "tr": "Keşke senin civarından geçmeseydim. Dün gece seni birkaç yabancı ile âşina gördüm."}, {"fa": "ای دل ظالم اسیر دام زلف او شدی\nشکر لله در بلایی مبتلا دیدم ترا", "tr": "Ey zalim gönül, onun zülfünün tuzağına esir oldun. Allah’a şükür seni bir belaya müptela gördüm."}, {"fa": "ای رقیب از دیدنت هرگز مرا ذوقی نشد\nغیر این ساعت که از جانان جدا دیدم ترا", "tr": "Ey rakip, canandan ayrı bulunduğun zamandan başka bir zaman yoktur ki seni görmekten zevk alabileyim."}, {"fa": "گر نه عاشق چه می گشتی بچشم اشکبار\nبر سر کویش فضولی بارها دیدم ترا", "tr": "Fuzuli, âşık değilsen niçin sevgilinin diyarında ağlaya ağlaya dolaşıyorsun? Seni birçok defalar orada gördüm."}, {"fa": "سویم شب هجران گذری نیست کسی را\nبر صورت حالم نظری نیست کسی را", "tr": "Hicran gecesi kimse semtime uğramıyor. Kimse hâlime dönüp bakmıyor."}, {"fa": "کس نیست که از تو خبری سوی من آرد\nمردم ازین غم خبری نیست کسی را", "tr": "Kimse yok ki bana senden bir haber getirsin. Ben bu gam içinde öldüm; kimsenin haberi yok."}, {"fa": "نگذاشت اثر در رهت از هستی من غم\nدر دل ز غم من اثری نیست کسی را", "tr": "Gam beni öyle mahvetti ki senin yolunun üzerinde benden bir eser bırakmadı. Kimsenin kalbinde bizim gamımızdan bir eser yok."}, {"fa": "گر محنت من نیست کسی را عجبی نیست\nمثل تو بت عشوه گری نیست کسی را", "tr": "Kimse benim kadar mihnete müptela değilse hayret etme. Kimin senin gibi bir işveli sevgilisi (putu) var ki?"}, {"fa": "ای شوخ جفا پیشه وفا ورز و گرنه\nآزردن و کشتن هنری نیست کسی را", "tr": "Ey cefakâr güzel, vefakâr olmağa çalış. Yoksa incitmek, öldürmek kimsenin nazarında bir hüner, bir marifet değildir ki!"}, {"fa": "آتش بجگرها زده عشق تو و حالا\nتا عشق تو ورزد جگری نیست کسی را", "tr": "Senin sevgin ciğerlere ateş salmıştır. Senin aşkına yakalanan kimsede ciğer kalmaz."}, {"fa": "از ترک تعلق مکن اندیشه فضولی\nدر راه تجرد خطری نیست کسی را", "tr": "Fuzuli, dünyadan alakanı kesmek hususunda hiç düşünme. Bu tecerrüt yolunda kimse için bir tehlike yoktur."}, {"fa": "بر باد مده سلسله مشک فشان را\nمگشای ز پیوند تنم رشته جان را", "tr": "Miskler kokan saçlarını rüzgâra verme. Tenimin örgüsünden can ipini çözme."}, {"fa": "راز تو نهانست مرا در دل و ترسم\nچشم ترم اظهار کند راز نهان را", "tr": "Senin sırrın benim gönlümde gizlidir. Korkuyorum, yaşlı gözüm gizli sırrımı açığa vuracak."}, {"fa": "رخساره بهر کسی منما فتنه مینگیز\nاز هم مگشا رابطه نظم جهان را", "tr": "Yüzünü herkese gösterip fitne koparma. Cihanın intizamını bozma (cihan nazmının rabıtasını çözme: kıyamet)."}, {"fa": "آه از دل شیدا که سراسیمه اویم\nدر عاشقی ما چه گناهست بتان را", "tr": "Ah bu deli gönül yok mu, beni böyle mecnun, perişan eden odur. Bu âşıklığımızda güzellerin (putların) ne günahı var ki!"}, {"fa": "ای کاش دلم خون شود از عشق بر آیم\nتا چند کشم محنت هر غنچه دهان را", "tr": "Keşke yüreğim kan olsa da bu aşktan kurtulsam. Daha ne kadar her gonca ağızlı dilberin kadrini çekeyim."}, {"fa": "ای بخت بخاک در آن گلرخم افکن\nمگذار که در خاک کشم حسرت آن را", "tr": "Ey baht, beni o gül yanaklının kapısının toprağına at (oraya gömüleyim); bana toprakta da (öldükten sonra da) onun hasretini çektirme."}, {"fa": "بر یاد خطش اشک روان ساز فضولی\nزان سبزه تر قطع مکن آب روان را", "tr": "Fuzuli, onun yanağındaki ayva tüylerini hatırlayıp gözyaşı dök. O taze sebzeden akar suyu kesme…"}, {"fa": "رسم زهد و شیوه تقوی نمی دانیم ما\nعشق می دانیم و بس اینها نمی دانیم ما", "tr": "Biz zâhitlik, sofuluk usul ve âdâbını bilmeyiz. Biz aşk biliriz ve tabiatıyla bunları bilmeyiz."}, {"fa": "نیست ما را در جهان با هیچ کاری احتیاج\nهیچ کاری غیر استغنا نمی دانیم ما", "tr": "Cihanda hiçbir işe ihtiyacımız yoktur. Biz istiğnadan başka hiçbir iş bilmeyiz."}, {"fa": "ما نمی گوییم کاری نیست غیر از عاشقی\nهست صد کاری دگر اما نمی دانیم ما", "tr": "Biz “âşıklıktan başka bir iş yoktur” demiyoruz; daha yüzlerce iş vardır amma biz onları bilmeyiz."}, {"fa": "شیوه تقلید و رسم اعتبار از ما مجو\nکار و بار مردم دنیا نمی دانیم ما", "tr": "Başkasını taklit etmek, zamanın kıymet hükümlerine uymak… Böyle şeyleri bizden bekleme. Bu dünya halkının işidir gücüdür. Biz bunları bilmeyiz."}, {"fa": "هر چه غیر از عشق یار و لذت دیدار اوست\nزاهدا بالله مجو از ما نمی دانیم ما", "tr": "Ey zâhit, bizden sevgilinin aşkını ve onun güzelliği karşısında duyduğumuz zevki sor. Allah aşkına bizde başka bir şey arama! Çünkü bilmeyiz."}, {"fa": "مظهر سر حق و آیینه گیتی نما\nجز می صاف و رخ زیبا نمی دانیم ما", "tr": "Bizce Allah’ın sırrı ancak güzel yüzde tecelli eder. Dünyayı gösteren âyine (İskender’in aynesi) de ancak saf şaraptır. Bizim bildiğimiz budur."}, {"fa": "گفتم ای گلرخ فضولی مرد در کوی تو گفت\nکیست او در کوی ما او را نمی دانیم ما", "tr": "Ey gül yanaklı, “Fuzuli senin diyarında öldü” dedim. O, “Bizim diyarımızda olan kimdir, biz onu tanımıyoruz” dedi."}, {"fa": "نهفتن در دل و جان درد و داغ آن پریوش را\nتوانم گر توان پوشید با خاشاک آتش را", "tr": "Eğer ateş çörçöp ile örtülebilirse o perinin gönlümüzdeki, canımızdaki derdi ve yarası da gizlenebilir."}, {"fa": "ز سینه آه حسرت می کشم چون تیر از ترکش\nکه کی سازد تهی بر سینه ام آن ترک ترکش را", "tr": "O Türk güzeli ne zaman terkeşindeki okları benim göğsümde tüketecek diye terkeşten ok çeker gibi sinemden hasret âhı çekiyorum."}, {"fa": "منقش گشت رخسارم بخون چون لاله زار آن به\nکه مالم بر کف پای تو رخسار منقش را", "tr": "Gözümden akan kanlar yanağımı gelincik tarlasına çevirdi. Artık bu nakışlı yanağı ayağına sürmem lâzım."}, {"fa": "دلا زهد ریایی هیچ کس را خوش نمی آید\nشعار خود مکن بهر خدا این وضع ناخوش را", "tr": "Ey gönül, mürâî sofuluk kimsenin hoşuna gitmez. Allah aşkına böyle tatsız şeyler yapıp durma."}, {"fa": "شبی دیدم که در زلف تو دل سرگشته می گردد\nنمی دانم چه تعبیرست این خواب مشوش را", "tr": "Bir gece rüyamda gönlümün senin zülfünde avare, perişan dolaştığını gördüm. Bilmiyorum bu karışık rüyayı neye yorayım?"}, {"fa": "گهی جورست و گه کم التفاتی کار آن بدخو\nبدور او بلا کم نیست عشاق بلاکش را", "tr": "O huysuz sevgili kah cevr ü cefa eder, kah iltifatını azaltır. Onun devrinde bela çeken âşıklara bela eksik değildir."}, {"fa": "فضولی چند در بند جهات مختلف مانی\nز غم بگذار تا بر هم زند ضعف تو هر شش را", "tr": "Fuzuli, daha ne kadar bu altı cihetin bağları içinde kalacaksın. Gamdan eriyip ortadan çık. Bu za‘ftan eriyip yok olman altısını da birbirine çarptırıp mahvetsin."}, {"fa": "عشق مضمون خط لوح جبین است مرا\nسرنوشت از قلم صنع همین است مرا", "tr": "Aşk benim alın yazımın manasıdır. Yaradılış kalemi bana bunu takdir etmiştir (sernüvişt)."}, {"fa": "روی بر راه سگ کوی تو سودن صد ره\nبهتر از سلطنت روی زمین است مرا", "tr": "Senin köpeğinin yoluna yüzümü sürmek bana dünya saltanatından yüz kere daha iyidir."}, {"fa": "ترک کوی تو نمی گیرم اگر می میرم\nروضه کوی تو فردوس برین است مرا", "tr": "Ölsem de senin diyarını terk etmem. Senin diyarının bahçesi (ravzası) benim için cennettir."}, {"fa": "در ره عشق تو گر بی دل و دینم چه عجب\nچشم مست تو بلای دل و دین است مرا", "tr": "Senin sevginin yolunda gönlümü ve dinimi kaybettimse hayret etme. Senin sarhoş gözün benim gönlümün ve dinimin belasıdır."}, {"fa": "بگمانی که شود وصل میسر یا نه\nزار مردن بغم هجر یقین است مرا", "tr": "Visal müyesser olur mu, olmaz mı şüphesi içinde ayrılık ıstırabı ile ağlayarak öleceğimi gayet iyi biliyorum."}, {"fa": "می دهم جان بامیدی که مگر دور شود\nغم هجر تو که درجان حزین است مرا", "tr": "Mahzun canım içinde senin ayrılığının ıstırabı var. Belki bu ıstıraptan kurtulurum ümidiyle can veriyorum."}, {"fa": "داغ دل گشت فضولی سبب سلطنتم\nکه ازو ملک جنون زیر نگین است مرا", "tr": "Fuzuli, bu gönül yarası benim saltanatıma sebep olmuştur. Bu sebepten cünun mülkü benim emrim (yüzüğüm) altındadır."}, {"fa": "هست می گویند خالی آن غدار آل را\nچشم کی برداشتم ز ابرو که بینم خال را", "tr": "O al yanak üstünde bir ben var diyorlar. Gözümü kaşlarından ayırabildim mi ki beni göreyim."}, {"fa": "چشم بگشادی ندیدم مرغ دل را جای خود\nغالبا شد صید آن شبها ز مشکین بال را", "tr": "Gözünü açtın; can kuşunu yerinde görmedin. Galiba o siyah kanatlı (müşkin) şahin onu avladı."}, {"fa": "ای بهر نوک مژه برده دلی در خواب او\nجمع کن یک لحظه دلهای پریشان حال را", "tr": "Ey her kirpiğinin ucuna bir gönül bağlanmış olan güzel, uyu da bir lahza bu hâli perişan gönülleri huzura kavuştur (topla)."}, {"fa": "هفته شد دیدن آن مه نشد روزی مرا\nآه اگر زین گونه در غم بگذرانم سال را", "tr": "Bir hafta oldu o ay yüzlü dilberi görmek nasip olmadı. Eyvah, eğer bir sene böylece gam içinde geçerse ben ne yaparım?"}, {"fa": "گفتمش با قد خم زان خال دور افتاده ام\nگفت با کی نیست گر نقطه نباشد دال را", "tr": "Ona dedim ki: Bu bükülmüş belimle o güzel benden uzak düştüm. Şöyle cevap verdi: Dâl harfinin üzerinde nokta olmazsa ne çıkar?"}, {"fa": "با تو خوش حالیم در دشت جنون ای دود آه\nکم مفرما از سر ما سایه اقبال را", "tr": "Ey âh dumanı, bu sevda çölünde sen bize biraz huzur ve rahat verebiliyorsun. Başımızın üzerinden ikbal gölgesini eksiltme."}, {"fa": "می جهد چشم فضولی وین ز موج اشک نیست\nغالبا می‌بینم آن رخسار فرخ‌فال را", "tr": "Fuzuli, gözüm seğiriyor; fakat bu gözyaşı dalgasından değildir. Galiba o mübarek, uğurlu yüzü göreceğim."}, {"fa": "شد بدیدار تو روشن دیده خونبار ما\nیافت از وصل تو مرهم سینه افکار ما", "tr": "Kanlı yaş döken gözümüz senin cemalinle aydınlandı. Yaralı gönlümüz senin visalinle şifa buldu (merhem buldu)."}, {"fa": "بی تردد دولت وصل تو ما را شد نصیب\nبه که غیر از شکر این نعمت نباشد کار ما", "tr": "Öteye beriye koşmadan, çırpmadan visalinin devleti bize nasip oldu. Artık bizim bu nimete şükretmekten başka bir işimiz kalmamalıdır."}, {"fa": "همدم ما بود غم درد سر از ما کرد کم\nغالبا دلگیر شد از گریه بسیار ما", "tr": "Gam bizim arkadaşımız idi. Taciz olup yanımızdan çekildi (eksildi). Galiba bizim fazla ağlamalarımız onun canını sıktı."}, {"fa": "دور گردون بر مراد خاطر ما شد مگر\nرحمی آمد چرخ را بر نالهای زار ما", "tr": "Dünya gönlümüzün istediği gibi dönmeye başladı. Acaba felek bizim feryad ü zârımıza acıdı mı dersin?"}, {"fa": "آسمان دارالشفای عافیت را در گشود\nرو نهاد از درد در صحت دل بیمار ما", "tr": "Gökyüzü âfiyet (felaketlerden masun kalmak) hastahanesinin kapısını açtı. Hasta gönlümüz dertten kurtulup sıhhate doğru yöneldi."}, {"fa": "بعد ازین ما را ز دوران فلک خوفی نماند\nدست دوران فلک کوته شد از آزار ما", "tr": "Bundan sonra feleğin devranından korkumuz kalmadı. Feleğin devranı artık bizi incitmekten el çekti."}, {"fa": "حمدلله بر فروغ صبح دولت یافت ره\nدر شب محنت فضولی دولت بیدار ما", "tr": "Allah’a şükür, Fuzuli, bizim uyanık saadetimiz mihnet gecesinde devlet sabahının aydınlığına kavuştu."}, {"fa": "باز خونبارست مژگانم نمی‌دانم چرا\nاضطرابی هست در جانم نمی‌دانم چرا", "tr": "Niçin bilmiyorum, gene kirpiklerimden kanlar akıyor; canımda bir çırpınma, bir ıstırap var, niçin bilmiyorum."}, {"fa": "عالمی بر حال من حیران و من بر حال خود\nمانده‌ام حیران که حیرانم نمی‌دانم چرا", "tr": "Âlem bana hayret ediyor. Ben de niçin hayret içinde olduğumu bilmediğimden kendi hâlime hayret ediyorum."}, {"fa": "روزگاری شد که بدحال و پریشانم ولی\nبس که بدحال و پریشانم نمی‌دانم چرا", "tr": "Uzun zamandır hâlim kötüleşti; perişan oldum. Fakat hâlim o derece kötüleşti, o derece perişan oldum ki sebebini dahi bilemiyorum."}, {"fa": "یار می‌دانم که می‌داند دوای درد من\nلیک می‌گوید نمی‌دانم نمی‌دانم چرا", "tr": "Biliyorum ki sevgilim benim derdimin devasını biliyor. Fakat “bilmiyorum” diyor. Niçin, bilmiyorum."}, {"fa": "نی وصالم می‌رهاند از مصیبت نی فراق\nدر همه اوقات گریانم نمی‌دانم چرا", "tr": "Beni felaketten ne visal ne de ayrılık kurtarıyor. Bilmiyorum niçin, her an ağlıyorum."}, {"fa": "نیست کاری کآید از من هر طرف بی‌اختیار\nمی‌دواند چرخ گردانم نمی‌دانم چرا", "tr": "Benim elimden hiçbir iş gelmez. Bir şeye yaramam. Bu dönen felek, bilmiyorum niçin irademi de elimden almış, beni dört tarafa koşturup…"}, {"fa": "درد خود را گرچه می‌دانم فضولی مهلک است\nفارغ از تدبیر درمانم نمی‌دانم چرا", "tr": "Fuzuli, gerçi derdimin mühlik olduğunu biliyorum. Fakat bilmiyorum niçin onun tedavisini düşünmüyorum bile!"}, {"fa": "درد رسوایی نخواهد داشت درمان ای طبیب\nخویش را رسوا مکن ما را مرنجان ای طبیب", "tr": "Ey tabip, bu rüsvâlık derdinin dermanı olmayacaktır. Bari kendini rüsvâ etme, bizi de incitme."}, {"fa": "هست بهبود تو در ترک علاج درد من\nچون ندارد فکر بهبود من امکان ای طبیب", "tr": "Ey tabip, mademki benim iyileşmeme imkân yoktur, o hâlde senin sıhhat ü afiyetin benim derdimi iyileştirmek için ilaç aramaktan vazgeçmektedir."}, {"fa": "خون گشودن از رگ تن چیست گر داری مدد\nشوق لعلش را برون آر از رک جان ای طبیب", "tr": "Vücudumdaki bir damardan kan almak iş değil ki ey tabip; lâ‘l’e benzeyen dudağının ateşli sevgisini canımın damarından çıkarabiliyor musun? O zaman bana bir iyilik etmiş olursun."}, {"fa": "هیچ شربت نیست بهر دفع سودایم مفید\nغیر یاد وصل و ذکر لعل جانان ای طبیب", "tr": "Ey tabip, cananımın visalini yâd etmek, onun lâ‘l’e benzeyen dudağını anmaktan gayrı hiçbir ilaç (şerbet) benim hastalığımı (sevda) gideremez."}, {"fa": "می کشی از غم مرا ظاهر بکن بهر خدا\nدرد پنهان مرا پیش رقیبان ای طبیب", "tr": "Allah aşkına ey tabip, benim gizli derdimi rakiplerime bildirme; beni gamdan öldürürsün."}, {"fa": "من نمی خواهم که این درد دل پنهان من\nبر تو هم ظاهر شود از تو چه پنهان ای طبیب", "tr": "Senden bir şey gizlenemez ey tabip; senin dahi bu gizli derdimi bilmeni istemiyorum."}, {"fa": "کشته است از غصه مانند تو صد بی درد را\nچاره درد فضولی نیست آسان ای طبیب", "tr": "Senin gibi yüz tane dertsizi kederden öldürmüştür; Fuzuli’nin derdine çare bulmak öyle kolay şey değildir ey tabip!"}, {"fa": "غمت در سینه ام جا کرد چون بیرون شود یارب\nو گر ماند چنین حال دل من چون شود یارب", "tr": "Aşkının ıstırabı sinemde yerleşti. Yarabbi, nasıl çıkar bilmem; eğer böyle kalırsa benim hâlim ne olur, Yarabbi."}, {"fa": "نمی خواهد شبی گیرم قراری بر سر کویش\nبلای بی قراری روزی گردون شود یارب", "tr": "Bir gece onun diyarında karar etmemi istemiyor. Yarabbi, felek bu bikararlık belasından kurtulmasın."}, {"fa": "جدا زان لعل میگون نیست کارم غیر خون خوردن\nبخون خوردن مرا آموخت این دل خون شود یارب", "tr": "O şarap renkli dudaktan ayrı kaldıkça mütemadiyen kan yutuyorum (kan yutmaktan başka işim yok). Yarabbi, bu gönül kan olsun; bana kan yutmayı öğretti."}, {"fa": "مرا گفتی که از من شاد خواهد شد دلت روزی\nندارم صبر گر خواهد شدن اکنون شود یارب", "tr": "Bana “bir gün gönlünü şad edeceğim” dedin. Sabredemiyorum. Yarabbi, olacaksa şimdi olsun."}, {"fa": "فلک در نامرادی تا بکی جانم بلب آرد\nمرادم کی میسر زان لب میگون شود یارب", "tr": "Felek ne zamana kadar beni muradıma nail etmeyecek. Bu ıstırap içinde beni ölüm hâline getirecek (canım dudağıma geldi). Yarabbi, ne zaman o şarap renkli dudaktan istediğim şeye nail olacağım."}, {"fa": "نمی‌خواهم که از من تنگدل گردد رقیب او\nاگر خواهد دلم محزون شود محزون شود یارب", "tr": "İstemem ki onun rakibi benim yüzümden müteessir olsun. Eğer gönlümün mahzun olmasını istiyorsa mahzun olsun, Yarabbi."}, {"fa": "فضولی قدر درد دل چه می‌دانند بی‌دردان\nمرا ذوقیست با این درد دل افزون شود یارب", "tr": "Fuzuli, dertsizler gönül derdinin kadrini ne bilirler. Ben bu gönül derdinden zevk alıyorum. Bu derdim artsın, Yarabbi."}, {"fa": "مرا ای شمع میل گریه شد در هجر یار امشب\nتو بنشین گریه دلسوز را با من گذار امشب", "tr": "Ey şem‘, bu gece yârın hicranı ile ağlamak istiyorum. Sen dur. Yürek yana yana ağlamayı bu gece bana bırak."}, {"fa": "بیاد شمع رویش خواهم از سر تا قدم سوزم\nبرو ای اشک آب از آتش من دور دار امشب", "tr": "Onun şem‘e benzeyen parlak yüzünü ana ana baştan aşağı yanmak istiyorum. Ey gözyaşı, git; bu gece ateşimden suyu uzaklaştır."}, {"fa": "فکندی وعده قتلم بفردا لیک می ترسم\nکه دیر آمد سحر من جان دهم در انتظار امشب", "tr": "Beni öldüreceğini vadetmiştin. Gene yarına bıraktın. Korkuyorum seher vakti gelir de ben bu gece intizar içinde can veririm."}, {"fa": "نهان از خلق دارم عزم کویش حسبة لله\nمرا رسوا مساز ای ناله بی اختیار امشب", "tr": "Onun diyarına gittiğimi herkesten saklıyorum. Allah aşkına ey nâle-i bî-ihtiyar (zaptı elde olmayan feryat), beni bu gece rüsvâ etme."}, {"fa": "مرا در گریه امروز نقد اشک شد آخر\nنمی دانم چه سازم گر رسد یارم نثار امشب", "tr": "Bugün ağlaya ağlaya gözyaşımın nakdi tükendi. Bu gece sevgilim gelirse bilmiyorum onun yoluna ne saçı saçacağım."}, {"fa": "متاع خواب را بربوده اند از مردم چشمم\nمگر بخت بد افکندست سوی من گذار امشب", "tr": "Gözümün bebeğinden (merdüm) uyku metaını yağma ettiler; galiba kötü tâli bu gece benim civarımdan geçti."}, {"fa": "فضولی را قراری بود شبها بر سر آن کو\nچو راندی از سر کویت کجا گیرد قرار امشب", "tr": "Fuzuli, geceleri senin diyarında karar ederdi (otururdu, rahat ederdi). Onu oradan kovdun; bu gece nerede karar edebilecek?"}, {"fa": "گر گریزم دم بدم بر آتش دل دیده آب\nبر چنین سوزی که دل دارد کی آرد سینه تاب", "tr": "Her an (dembeden) gönül ateşine gözüm su dökmeseydi, yürekteki ateşe sine nasıl tahammül ederdi?"}, {"fa": "بگسل ای سایه ز من تابی نداری بر جفا\nمی گریزی بر تو گر تیغی کشد آن آفتاب", "tr": "Ey gölgem, benden ayrıl; senin cefaya tahammülün yok. O güneş sana bir kılıç çekince hemen kaçıyorsun."}, {"fa": "تا نبیند آفتاب عارضش را سایه ام\nاز حسد خود را میان این و آن کردم حجاب", "tr": "Gölgem onun güneşler kadar parlak yanağını görmesin diye hasetten aralarına girip perde oluyorum."}, {"fa": "در نقاب آن روی و من با آه دل در حیرتم\nدر میان این دو آتش چون نمی سوزد نقاب", "tr": "(Ateş gibi kırmızı ve parlak) yüzü örtülü; ben karşısında âh ediyorum. Hayretteyim. O nikab bu iki ateş arasında nasıl yanmıyor."}, {"fa": "نیم بسمل کرده و دامن ز خونم می کشد\nمن از او در اضطرابم او ز من در اجتناب", "tr": "Beni yarı boğazlanmış hâlde bıraktı, kanımdan eteğini çekiyor. Ben onun yüzünden ıstırap içinde çırpınıyorum. O benden çekiniyor."}, {"fa": "آتش است آن شوخ و من شمع شبستان بلا\nگر رود میرم گر آید سوز دم با صد عذاب", "tr": "O güzel ateştir; ben ise beli yatak odasının mumu. Giderse ölürüm (sönerim), yaklaşırsa yüzlerce azap içinde beni yakar."}, {"fa": "سوخت آهم چرخ را من می خورم خوناب ازو\nنیست جز خونابه آتش را نصیبی از کباب", "tr": "Âhım çarhı ateşe verdi. Ben kan yutuyorum. Ateşin kebaptan nasibi kandan başka bir şey değildir."}, {"fa": "مردم چشمم فضولی شد سیه پوش از عزا\nغالبا شد کشته تیغ سهر در دیده خواب", "tr": "Fuzulî’nin gözbebeği kara matem elbiselerine büründü. Galiba uykusuzluk kılıcı gözündeki uykuyu öldürdü. O matem tutuyor."}, {"fa": "نیست تا صبح بجز فکر تو کارم همه شب\nکارم اینست جز این کار ندارم همه شب", "tr": "Gece sabahlara kadar yalnız seni düşünüyorum. Bütün gece işim budur. Bundan başka bir şey yapmıyorum."}, {"fa": "همه روزم شده شب اختر آن شبها اشک\nآه ازین درد چسان اشک نبارم همه شب", "tr": "Bütün günlerim gece oldu. O gecelerin yıldızları da gözyaşlarım. Ah bu dert yüzünden nasıl sabahlara kadar ağlamam."}, {"fa": "لطف کن یک شب و در کلبه من گیر قرار\nتا بدانی که چرا نیست قرارم همه شب", "tr": "Lütfet, bir gece kulübemde kal (karar et) ki niçin bütün gece böyle huzur ve sükûndan mahrum (bikarar) olduğumu anlayasın."}, {"fa": "تا ندانند که من بهر تو می نالم و بس\nبفغان از همه کس ناله برارم همه شب", "tr": "Yalnız ben senin için, senin sevginle feryat ediyorum sanmasınlar diye feryatlarımla herkesi ağlatıyor, inletiyorum."}, {"fa": "تا ز من پیش تو گویند حکایت همه روز\nهیچ کس را بفراغت نگذارم همه شب", "tr": "Gündüz akşama kadar senin yanında benden bahsetsinler, benim maceramı anlatsınlar diye bütün gece kimseyi rahat bırakmıyorum."}, {"fa": "بامیدی که اگر بشنود آن ماه شبی\nز فلک می گذرد ناله زارم همه شب", "tr": "O ay yüzlü sevgilim belki bir gece işitir diye gece sabahlara kadar nâle ve feryadım feleklerden aşıyor."}, {"fa": "شب تنهایی من نیست فضولی بی تو\nشمع بزمست خیال رخ یارم همه شب", "tr": "Yalnız gecelerimde Fuzulî sensiz değildir. Yârimin yüzünün hayali bütün gece meclisimizin aydınlığıdır (mumudur)."}, {"fa": "تندست یار و بی سببی می کند غضب\nدارد غضب همیشه بعشاق زین سبب", "tr": "Sevgilim pek hırçındır. Hiç sebepsiz hiddetlenir. İşte bu sebepten âşıklarına daima gazap eder."}, {"fa": "مطلوب را چو نیست مقام معینی\nهرگز نمی رسد بنهایت ره طلب", "tr": "İstenen şeyin muayyen bir haddi (makamı) olmadığı için arzu yolu bir türlü bitip tükenmez."}, {"fa": "معلوم می شود که ندارد مذاق عشق\nاو را که از حرارت می می رسد طرب", "tr": "Bir kimse şarabın hararetinden zevk alıp neşvelendi mi anlaşılır ki onda aşk zevki yoktur."}, {"fa": "کام از لبت چگونه بیابند جان و دل\nدل می رسد بجان ز تو جان می رسد بلب", "tr": "Can ve gönül nasıl senin dudağından muratlarını alabilirler. Senin yüzünden gönül can verecek hâle, can da dudağa gelir."}, {"fa": "تو چشمه حیاتی و ما ظلمت فنا\nاز ما هوای وصل تو امریست بس عجب", "tr": "Sen âb-ı hayat çeşmesi, biz ise fanilik zulümatıyız. Senin visalini istemek çok şaşılacak şeydir."}, {"fa": "ما طالب تو و تو گریزان ز قرب ما\nاینست کار ما و تو پیوسته روز و شب", "tr": "Biz seni istiyoruz, sen bize yaklaşmak istemiyorsun. Gece gündüz daima (senin ve benim) işimiz budur."}, {"fa": "الفت میان ما و تو بسیار مشکل است\nتو در پس حجابی و ما در ره ادب", "tr": "Bizim ikimizin aramızda görüşüp konuşmamız çok müşküldür. Çünkü sen perde arkasındasın, biz de edep yolundayız."}, {"fa": "چون غیر ممکن است فضولی وصال دوست\nبیهوده چند در طلبش می کشی تعب", "tr": "Fuzuli, mademki sevgiliye vasıl olmak imkânsızdır, ne zamana kadar onun peşinde koşup zahmet çekeceksin."}, {"fa": "ای همه دم بزم تو جای رقیب\nبهر تو ناچار لقای رقیب", "tr": "Ey sevgilim, rakip daima senin meclisindedir. Galiba rakibi görmeden olamıyorsun."}, {"fa": "امر محالست مرا از تو کام\nکام تو چون هست رضای رقیب", "tr": "Arzun rakibin rızasını elde etmek olduktan sonra bizim senden murat almamız imkânsızdır."}, {"fa": "جور و جفای تو برای منست\nمهر و وفای تو برای رقیب", "tr": "Senin cevr ü cefan bana, sevgi ve vefan da rakibe mahsustur."}, {"fa": "بر سر کوی تو گذر می کند\nچون ننهم روی بپای رقیب", "tr": "Senin diyarından geçiyor, oralarda dolaşıyor. Nasıl rakibin ayağına yüzümü sürmem."}, {"fa": "دیدن هر واقعه سهل است لیک\nسخت بلاییست بلای رقیب", "tr": "Her felakete kolayca katlanılır. Ama rakibin belası çetin bir beladır."}, {"fa": "زندگی اهل دل از وصل تست\nوصل تو موقوف فنای رقیب", "tr": "Ehl-i diller senin visalinle yaşayabilirler. Rakip ölmedikçe de senin visaline erişilmez."}, {"fa": "صبر و قرار تو فضولی کم است\nکمتر از آن مهر و وفای رقیب", "tr": "Fuzuli, senin sabr ü sükûnun azdır. Rakibin sevgi ve vefası ondan daha azdır."}, {"fa": "کی توانم رست در کویت ز غوغای رقیب\nمی کند فریاد سگ هر گه که می بیند رقیب", "tr": "Senin diyarında rakibin kavgasından nasıl kurtulabilirim. Köpek bir yabancı (garib) gördü mü hemen havlamaya başlar."}, {"fa": "وصل تو می خواهم ولی مشکل که بندد صورتی\nنیست اهل درد را از خوان وصل او نصیب", "tr": "Visal istiyorum, fakat elde etmek müşkül. Onun visali sofrasından dertlilerin nasibi yok."}, {"fa": "آتشی از شوق گل در دل ندارد غالبا\nچون نمی سوزد قفس را نالهای عندلیب", "tr": "Galiba gönlünde gülün hararetli sevgisinden bir ateş yok. Bülbülün feryatları nasıl kafesi yakmıyor."}, {"fa": "می نهد بر دست من دست از پی تشخیص لیک\nکی علاج درد من می آید از دست طبیب", "tr": "Hastalığımı teşhis etmek için elini elimin üstüne koyuyor. Yoksa derdime deva bulmak tabibin elinden gelir mi hiç?"}, {"fa": "ای که دم از دولت قرب سلاطین می زنی\nنیست ممکن این که آزاری نبینی عنقریب", "tr": "Ey sultanlara yakın olmak devletinden dem vuran adam, imkânı yok; pek yakında onlardan incineceksin."}, {"fa": "گه دل از جان می کند او را نهان گه جان ز دل\nنیست آن گل چهره در هر جا که باشد بی رقیب", "tr": "Onu bazen gönül candan, bazen de can gönülden gizler. O gül yüzlü nerede bulunursa bulunsun rakipsiz değildir."}, {"fa": "نیست مقصود دل بیچاره غیر از وصل یار\nفاستجب ما قد دعا عبد ضعیف یا مجیب", "tr": "Biçare gönlün tek isteği yâre vasıl olmaktır. Ey duaları kabul eden Tanrı, bu zayıf kulun istediğini ona ihsan et (duasını kabul et)."}, {"fa": "چند می پرسی فضولی بر که واله گشته\nوالهم کی می رسد بر خاطرم نام حبیب", "tr": "Kime hayransın diye daha ne kadar sorup duracaksın, Fuzuli? Ben kendimden geçmişim, sevgilimin adı hatırıma gelir mi?.."}, {"fa": "قرآن صفات جاه و جلال محمد است\nاحکام شرع شرح کمال محمد است", "tr": "Kur’an, Muhammed’in rütbe ve azametinin sıfatlarıdır. Şeriat hükümleri, Muhammed’in kemalinin şerhidir."}, {"fa": "اخبار انبیا که سراسر شنیده\nیک یک بیان حسن خصال محمد است", "tr": "Peygamberlerin baştan başa dinlediğin kıssaları teker teker Muhammed’in güzel huylarını beyan eder."}, {"fa": "آن کاف و نون که اصل وجودست خلق را\nکاف کمال و نون نوال محمد است", "tr": "Yaradılmışların varlığının aslı olan o hû nûrı (kün), Muhammed’in kemalinin (kâf)ı ve nevalinin (nimetinin) nûnudur."}, {"fa": "مدی که بر سر الف آدمست تاج\nمضمون میم و معنی دال محمد است", "tr": "(Âdem) kelimesinin elifinin üstünde tâc olan (med), Muhammed’in mîm ve dâlının mânasıdır."}, {"fa": "زیب صحیفه ازل و نسخه ابد\nاز نقطهای دانه خال محمد است", "tr": "Ezel sahifesinin ve ebed nüshasının süsü, Muhammed’in beninin danesinin noktalarındandır."}, {"fa": "سیاح درک باصره عقل کی رسد\nجایی که جلوه گاه جمال محمد است", "tr": "Muhammed’in güzelliğinin (cemalinin) tecelli ettiği yerlere idrak seyyahı ve akıl gözü erişebilir mi?"}, {"fa": "از دور نیست کار فضولی بانتظام\nاز دولت محمد و آل محمد است", "tr": "Fuzuli’nin işini böyle iyi şekilde yürüten, düzelten devran değildir. Muhammed ve onun âl ü evlâdının sayesinde onun işi düzgün gidiyor."}, {"fa": "جانم در آن آرزوی وصال محمد است\nچشمم در انتظار جمال محمد است", "tr": "Canım Muhammed’e kavuşmak istiyor. Gözüm onun cemalini gözlüyor."}, {"fa": "قدم خمیده چون فلک از جور دور نیست\nاز شوق روی ماه مثال محمد است", "tr": "Felek gibi benim belimi büken dünyanın cevri değildir. Muhammed’in aya benzeyen dirliğinin sevgisidir."}, {"fa": "جسمم ضعیف چون مه نو نیست از فلک\nبر یاد ابروی چو هلال محمد است", "tr": "Vücudum o kadar zayıf ki yeni aya benziyor. Fakat bu feleğin cefasından değildir. Muhammed’in hilale benzeyen kaşını hatırladıkça o hasret beni böyle eritiyor."}, {"fa": "این داغهای تازه که بر سینه منست\nاز اشتیاق دانه خال محمد است", "tr": "Göğsümdeki bu taze dağlar Muhammed’in beninin iştiyakındandır."}, {"fa": "شایسته احاطه تملیک غیر نیست\nملک دلم که وقف خیال محمد است", "tr": "Muhammed’in hayaline vakfettiğim bu gönül mülkü başkasına temlik edilmeğe layık değildir."}, {"fa": "آیینه دار طوطی نطقم هر آینه\nاندیشه صفات کمال محمد است", "tr": "Nutkumun papağanına ayna tutan elbette Muhammed’in kemalinin sıfatlarını düşünmektir."}, {"fa": "کردست مهر غیر فضولی ز دل برون\nتا عاشق محمد و آل محمد است", "tr": "Fuzuli, Muhammed’e ve onun âl ü evlâdına âşık olalı beri başka sevgileri gönlünden çıkarıp atmıştır."}, {"fa": "ماه من نخل قدت سرو خرامان منست\nسرو من ماه رخت شمع شبستان منست", "tr": "Ay yüzlü sevgilim, senin fidana benzeyen boyun benim sahramın servimdir. Benim servi boylu güzelim, aya benzeyen yanağın benim yatak odamın mumudur."}, {"fa": "می کند حال مرا هجر تو بد وصل تو خوش\nهجر تو درد من و وصل تو درمان منست", "tr": "Sana kavuştukça bahtiyar, senden ayrıldıkça bedbaht oluyorum. Ayrılığın bana dert, visalin derman oluyor."}, {"fa": "دل اسیر قد و جان مست می لعل تو شد\nقامتت کام دل و لعل لبت جان منست", "tr": "Gönlüm boyuna bosuna esir, canım la‘l dudağının şarabından sarhoş oldu. Endamın gönlümün muradı, la‘l dudağın benim canımdır."}, {"fa": "دور بادا قد جانان من از چشم بدان\nسرو گلزار نکویی قد جانان منست", "tr": "Cananımın boyu bosu kötü gözden ırak olsun. Zira benim cananımın endamı iyilik gülistanının servisidir."}, {"fa": "گفتم ای شمع بتان جای دلم دام بلاست\nگفت کان دام بلا دور زنخدان منست", "tr": "Ey güzellerin (putların) mumu, dedim, gönlümün yeri bela tuzağıdır. O bela tuzağı benim çenemin yuvarlağıdır, dedi."}, {"fa": "پرده از راز دل زار من افتاد و سبب\nچشم گریان من و چاک گریبان منست", "tr": "Ağlayan (zâr) gönlümün sırrının (râz) üzerindeki perde kalktı (âleme faş oldu). Buna sebep ağlamam (ağlayan gözüm) ve ıstıraptan yakamı parçalamamdır."}, {"fa": "برد آرام فضولی قد آن سرو روان\nگرچه آرام دل زار پریشان منست", "tr": "Fuzuli’nin bu ağlayan perişan gönlü ancak o servi-revânın huzuruyla biraz sükûn ve rahata kavuşabilir. Fakat gene o endam benim huzur ve rahatımı mahvetti."}, {"fa": "بهر صید آن ترک بدخو بر سمند کین نشست\nباز خواهد شد عنان صبر صد مسکین ز دست", "tr": "O hırçın, yavuz güzel avlanmak için doru renkli atına bindi. Gene yüzlerce biçare âşık sabır dizginini elinden kaçıracak."}, {"fa": "دید چون لطف جمالت باز بر هم زد زرشک\nنقش بند دهر در گلزار هر آیین که بست", "tr": "Dünya ressamı senin cemalindeki letafeti (ele avuca girmeyen güzellik) görünce hasedinden gülzarda her tarzda ne eser vücuda getirdiyse hepsini mahvetti."}, {"fa": "گر خورد خونابه دل بر یاد لعلت دور نیست\nباده پندارد اگر آبی دهی رنگین به مست", "tr": "Gönül, la‘l dudağını anarak kan yutuyorsa bu şaşılacak bir şey değildir. Sarhoşa renkli bir su verirsen şarap sanır."}, {"fa": "پرده افکندی ز عارض ماه را تابی نماند\nچین گشادی زلف را بازار مشک چین شکست", "tr": "Yanağından nikabı kaldırdın; ayda da nur (ziya, kudret, tab) kalmadı. Zülfünün örgüsünü (çîn) açtın; Çin miskinin revacı kırıldı (revaçtan düştü)."}, {"fa": "تیشه فرهاد بهر کوه کندن بس بود\nشعله کو آتش او در غم شیرین بجست", "tr": "Dağ kazmak için Ferhad’ın kazmaya ihtiyacı yoktur. Şirin’in aşkı içinde çektiği ıstırap ateşinden yükselen bir alev işi yapar."}, {"fa": "نیست عالم را ثباتی پیش اهل اعتبار\nهست بر آب روان نقش حبابی این که هست", "tr": "İbret gözüyle bakanların nazarında âlem sebatsız bir varlıktır. Bu mevcudat akan su üzerindeki habablar gibidir."}, {"fa": "من نه تنها باختم در راه آن بت نقد دین\nزان بت بی دین فضولی هیچ اهل دین نرست", "tr": "Fuzûlî, o güzelin (putun) uğrunda din nakdini kaybeden yalnız ben değilim. O dinsiz güzelin (putun) elinden hangi din ehli kurtulabildi."}, {"fa": "صیقل آیینه دلها نم چشم ترست\nهر کرا نمناک تر دیده دلش روشن ترست", "tr": "Gönüller aynasının cilası yaşlı gözdür. Kimin gözü daha yaşlı ise onun yüreği daha aydındır."}, {"fa": "روز نومیدی مراد از قطرهای اشک جو\nرهبر گم گشتگان در ظلمت شب اخترست", "tr": "Ümitsizlik gününde muradım gözyaşı damlalarından iste. Gece karanlığında yolunu kaybedenlerin kılavuzu yıldızlardır."}, {"fa": "گریه کن آب چشمی ریز گر صاحب دلی\nکآدمی بی اشکی و آهی درخت بی برست", "tr": "Eğer gönül sahibi isen ağla, gözyaşı dök. Gözyaşı dökmeyen, âh etmeyen insan meyvesiz ağaçtır."}, {"fa": "دل که مملو از هوای دوست باشد چون حباب\nجلوه اش بالای دریای سپهر اخضرست", "tr": "Sevgilinin aşkı havası ile dolu olan gönül, yeşil gök denizinin üzerinde beliren hababa benzer."}, {"fa": "چهره زردی نما در عشق کین رنگ لطیف\nکارسازیهای بازار محبت را زرست", "tr": "Aşk yolunda sararmış yüz göster. Zira bu güzel renk, muhabbet pazarının işler beceren altınıdır."}, {"fa": "شمع گر پرورد آتش را سزای خویش یافت\nنیست خالی از ندامت هر که دشمن پرورست", "tr": "Mum, eğer ateşi besleyip büyüttüyse neticede layıkını buldu. Düşmanını besleyen daima pişman olur."}, {"fa": "چون حباب از اشک جابر آب دارد متصل\nتا فضولی را هوای سرو قدت در سرست", "tr": "Fuzuli’nin başında senin servi boyunun sevdası (havası) bulundukça onun yeri daima habab gibi gözyaşı suyunun üzerindedir."}, {"fa": "عمر دراز من که پریشان گذشته است\nدر آرزوی گیسوی جانان گذشته است", "tr": "Benim perişan geçen uzun ömrüm sevgilimin saçının arzu ve hasretiyle geçmiştir."}, {"fa": "ذوق وصال اگر نشناسیم دور نیست\nاوقات ما همیشه به هجران گذشته است", "tr": "Visal zevkini bilmiyorsak bu tabiidir. Zira vaktimiz daima hicran içinde geçmiştir."}, {"fa": "داریم آتشی ز تو در دل که سوختست\nغیر تو هر که در دل سوزان گذشته است", "tr": "Senin yüzünden içimizde öyle bir ateş yanıyor ki o ateş yanan gönlümüzde senden başka ne varsa hepsini yakıp mahvetmiştir."}, {"fa": "در دل گذشته است خیال اجل مرا\nهر جا که ذکر غمزه جانان گذشته است", "tr": "Her nerede cananımın gamzesinden bahsedilmişse o anda gönlümden ecelin hayali geçmiştir."}, {"fa": "بگذر طبیب از سر درمان درد من\nبیمار درد عشق ز درمان گذشته است", "tr": "Ey tabib, derdime derman bulmak sevdasından vazgeç. Aşk derdinin hastası dermandan geçmiştir."}, {"fa": "هر دم بناوک تو که در جان گرفته جا\nدل میل می کند مگر از جان گذشته است", "tr": "Canıma saplanan okunla ben öldüm. Gönül de onu istiyor. Yoksa canından bezdi mi (yoksa candan geçmiştir)."}, {"fa": "زاهد ز ما مجو سر و سامان که مست عشق\nز اندیشه بی سر و سامان گذشته است", "tr": "Zâhit, bizde mal mülk hevesi arama. Aşktan sarhoş olan baş mal mülkten geçmiştir."}, {"fa": "افغان ز چرخ گر گذرانی چه فایده\nچون کار تو فضولی از افغان گذشته است", "tr": "Fuzuli, isterse figanın, feryadın göklerden aşsın, ne faide? Senin işin artık feryad ü figandan geçmiştir."}, {"fa": "ای دل بسی ز محنت هجران نمانده است\nخوش باش کین معامله چندان نمانده است", "tr": "Ey gönül, artık hicran mihneti tükenmek üzeredir. Gönlünü ferah tut. Çoğu gitti, azı kaldı."}, {"fa": "جان را ز بیم هجر بجانان سپرده ام\nهجری میانه من و جانان نمانده است", "tr": "Hicrandan korktuğum için canımı cananıma verdim. Artık benimle cananım arasında hicran derdi kalmamıştır."}, {"fa": "از اشک چشم تر زده آبی بر آتشم\nسوزی که داشت سینه سوزان نمانده است", "tr": "Yaşlı gözüm ateşim üzerine su serpti. Yanan gönlüm artık yanmıyor."}, {"fa": "امیدواریی که دل از یار داشت هست\nاندیشه که بود ز حرمان نمانده است", "tr": "Yârdan ümidim var, mahrumiyetten korkum kalmamıştır."}, {"fa": "شکر خدا ز درد سرم رسته اند خلق\nدر من ز ضعف طاقت افغان نمانده است", "tr": "Allah’a şükür, zaiflikten feryada takatim kalmadı da halk benim tacizimden kurtuldu."}, {"fa": "دوران نموده است مداری بکام دل\nدل را شکایت از غم دوران نمانده است", "tr": "Devran bir kere gönlümün istediği gibi döndü. Artık gönlümün dünya gamından şikâyeti kalmadı."}, {"fa": "حیران آن جمال نه چشم منست و بس\nچشمی نمانده است که حیران نمانده است", "tr": "O güzelliğin karşısında hayran kalan yalnız benim gözüm değildir. Bir göz kalmamıştır ki ona hayran kalmamış olsun."}, {"fa": "جان دادنم به مژده وصل تو آرزوست\nبا آنکه در فراق توام جان نماند است", "tr": "Ayrılığın bende can bırakmadı. Buna rağmen senin visalini müjdeleyene canımı vermek istiyorum."}, {"fa": "دارم فضولی از غم عالم فراغتی\nگویا دلی که بود مرا آن نمانده است", "tr": "Fuzuli, artık dünya gamından elimi eteğimi çektim. Sanki evvelce bir gönlüm vardı, şimdi ondan eser kalmamış (ben o hâldeyim)."}, {"fa": "پیش عاقل قصه درد من و مجنون یکیست\nاختلافی در سخن باشد ولی مضمون یکیست", "tr": "Akıllı insan nazarında Mecnun’un macerası ile benimki arasında fark yoktur. Yalnız kelime değişiyor; mana aynı."}, {"fa": "داغ دل را خواستم مرهم رساندی ناوکی\nغالبا پنداشتی داغ دل پرخون یکیست", "tr": "Gönlümün yarasına merhem istiyordum, bir tek ok attın. Galiba kana boğulmuş gönlümde bir tek yara var sandın."}, {"fa": "شرط حسنست آن که طاق آن خم ابرو دو تاست\nرسم خطست این که خال آن لب میگون یکیست", "tr": "O kaş bükümünün takının (tek) ikiye ayrılması güzelliğin şartıdır. O şarap renkli dudağın bir tek beni olması da yazı icabıdır (leb kelimesinde bir tek nokta vardır)."}, {"fa": "جای سرو قامتت در جان نمی گیرد الف\nحاش لله کی الف با آن قد موزون یکیست", "tr": "Can kelimesinin ortasındaki dâl (canda) senin servi boyunun yerini tutmuyor. Hâşâ, sümme hâşâ, elif o âhenkli (mevzun) endam ile bir olabilir mi?"}, {"fa": "نیست در دل غیر تو زان رو عزیزی بر دلم\nقدر دارد در صدف هر گه در مکنون یکیست", "tr": "Gönülde senden başka bir sevgili yoktur; o yüzden sen benim için çok azizsin. Sedef içinde değerli inci tek oldu mu kıymeti fazladır."}, {"fa": "نیستم بلبل که هر ساعت نهم دل بر گلی\nزین همه گل چهره مقصود من محزون یکیست", "tr": "Bülbül değilim ki her saat bir güle gönül vereyim. Bütün bu gül yüzlüler içinde ben mahzunun istediği bir danedir."}, {"fa": "بر سر من هر که می بیند فضولی دود آه\nآن تصور می کند کین هم ز نه گردون یکیست", "tr": "Fuzuli, başımın üzerindeki âh dumanını gören herkes bunu dokuz kat felekten birisi zanneder."}, {"fa": "زلال فیض بقا رشحه ز جام منست\nحیات باقی من نشاه مدام منست", "tr": "Bâkî feyzinin saf suyu (zülâl) benim kadehimden bir katredir (sızıntı). Benim bâkî hayatım şarabımın neş’esidir (daimî neş’emdir)."}, {"fa": "بمن فرشته کجا می رسد ز رفعت قدر\nحریم درگه پیر مغان مقام منست", "tr": "Melek kadr ü kıymet bakımından benim kadar yüksek olabilir mi? Pîr-i mugânın dergâhının harimi benim makamımdır."}, {"fa": "مراست حرمتی از فیض می که پیر مغان\nمدام خم شده از بهر احترام منست", "tr": "Şarabın feyzi bana öyle muhterem bir mevki temin etmiştir ki pîr-i mugân daima bana hürmeten önümde eğilir."}, {"fa": "چو من نبوده کسی را ز عشق بدنامی\nهمیشه خطبه این سلطنت بنام منست", "tr": "Kimse benim kadar aşk uğrunda bednâm olmamıştır. Bu saltanatın hutbesi daima benim namıma okunur."}, {"fa": "ز دیده دور کن ای اشک خار مژگان را\nکه جلوه گاه سهی سرو خوش خرام منست", "tr": "Ey gözyaşı, gözümden kirpik dikenini söküp at. Zira orası benim güzel güzel salınan uzun boylu selvimin dolaştığı yerdir."}, {"fa": "بمشک سر چو بنفشه فرو نمی آرم\nکنون که بوی ازان زلف در مشام منست", "tr": "Şimdi burnumda o zülfün kokusu vardır; menekşe gibi miske baş eğmem."}, {"fa": "فتاده است فضولی بدستم آن خم زلف\nهزار شکر که دور فلک بکام منست", "tr": "Fuzuli, elime o zülfün kıvrımı düştü. Binlerce şükrolsun ki felek gönlümün istediği gibi devrediyor."}, {"fa": "سرو را همچو قدت شیوه رعنایی نیست\nاین قدر هست که او مثل تو هرجایی نیست", "tr": "Servde senin boyun bosun kadar güzellik edası yoktur. Yalnız şu kadar var ki o senin gibi hercai (her yere giden) değildir."}, {"fa": "سرو و گل تا ز قد و روی تو دیدند شکست\nباغبان را سر و برگ چمن آرایی نیست", "tr": "Servi ile gül senin endamın ve yanağın yanında revacını kaybettikten beri bahçıvanın çimenleri çiçeklerle süslemek hevesi kalmadı."}, {"fa": "همدمی چون غم او نیست دم تنهایی\nهر کرا هست غم او غم تنهایی نیست", "tr": "Kimsesizlik anında onun aşkının ıstırabı gibi bir arkadaş olamaz. Kimde bu ıstırap varsa onda kimsesizlik derdi yoktur."}, {"fa": "ای دل از عاشقی از طعنه میندیش که ذوق\nنتوان یافت ز عشقی که برسوایی نیست", "tr": "Ey gönül, âşık isen ta‘n ü teşniden korkma. Zira insanı âleme rüsvâ etmeyen bir aşkta zevk bulunmaz."}, {"fa": "ای که داری سر سودای تجارت بی نفع\nهیچ سرمایه به از جوهر دانایی نیست", "tr": "Ey hiçbir kâr temin etmeden ticaret etmek isteyen adam, bu iş için ilim cevherinden daha iyi sermaye olamaz."}, {"fa": "صبر در عشق تو کاریست پسندیده ولی\nکرده ام تجربه کار من شیدایی نیست", "tr": "Senin aşkında sabır çok makbul bir iştir. Fakat tecrübe etmişim: bu benim gibi bir delinin kârı değildir."}, {"fa": "مشو از دیده خونبار فضولی غایب\nکه درو بی گل روی تو شکیبایی نیست", "tr": "Fuzuli’nin kanlı yaş döken gözünden kaybolma. Zira senin gül yanağından uzak kalırsa o sabredemez."}, {"fa": "مه دلاک من آیینه اهل نظر است\nهر زمان صید کسی کرده بشکل دگر است", "tr": "Benim sevgilim ay yüzlü dilber. Nazar sahibi insanların aynasıdır. Her an bir başka tarzda, bir başka eda ile birisini avlar."}, {"fa": "در تمنای وصال دم تیغش همه دم\nعاشقان را تن چون موی بخونابه تر است", "tr": "Onun usturasının ağzına kavuşabilmek arzusuyla her an âşıklarının kıl gibi zayıf vücudu gözyaşıyla ıslatılmıştır."}, {"fa": "همه را غرقه بخونست دل از غمزه او\nرگ جان همه را غمزه او نیشتر است", "tr": "Herkesin gönlü onun yan bakışından kana boğulmuştur. Herkesin can damarına onun gamzesi neşterdir."}, {"fa": "بکفی تیغ گرفته بکفی سنگ مدام\nبر سر عربده با عاشق خونین جگر است", "tr": "Bir elinde ustura, bir elinde taş, daima ciğeri kan olmuş bir âşık ile mücadelededir."}, {"fa": "پایمال الم از تیغ ستمکاری اوست\nتن عشاق که باریک تر از موی سر است", "tr": "Âşıkların baştaki saçtan daha ince olan vücutları onun zulüm usturasına uğrayıp elem ayakları altına serilmiştir."}, {"fa": "آهم از چرخ برین می گذرد در غم او\nآه ازین غم که ز حال دل من بی خبر است", "tr": "Onun sevgisinin derdiyle âhım felekleri aşmıştır. Fakat asıl ona yanıyorum ki o benim gönlümün bu hâlinden haberdar değildir."}, {"fa": "چاک چاکست ز غم سینه ما چون شانه\nوه که ملک دل ما را غم او رخنه گر است", "tr": "Göğsümüz tarak gibi aşk ıstırabı ile parça parçadır. Yazık ki bizim gönlümüzün mülkünü onun sevgisinin derdi sakatladı, harap etti."}, {"fa": "نشود قطع بمقراض جفا پیوندش\nبس که پیوسته دلم بسته آن سیمبر است", "tr": "Gönlüm o gümüş sineli dilbere öyle bağlanmıştır ki bu alâkayı cefa makası ile kesmek kabil değildir."}, {"fa": "هوسی در سرت افتاد فضولی زان مه\nحذری کن که درین واقعه سر در خطر است", "tr": "Fuzuli, o ay gibi parlak güzel için aklından bir şeyler geçiyor. Aman sakın, bu işte başın tehlikeye girer."}, {"fa": "خورشید بسی خاک‌نشین شد به هوایت\nروزی نتوانست که بوسد کف پایت", "tr": "Güneş senin aşkınla nice zamanlar topraklar üzerinde serili kaldı da bir gün ayağını öpmeye muktedir olmadı."}, {"fa": "فریاد که جانم به لب آمد ز تحیر\nحرفی نشنیدم ز لب روح‌فزایت", "tr": "Buna ağlıyorum ki hayretten canım dudağıma geldi de senin can artıran dudağından bir söz işitmedim."}, {"fa": "خون ریخته بهر ثواب از همه جز ما\nما را گنه اینست که مردیم برایت", "tr": "Sevaba girmek için bizden başka herkesin kanını dökmüşsün. Biz senin yolunda öldük. Günahımız bu."}, {"fa": "زان غافلی ای ماه که هر شب به تردد\nچون هاله رهی می‌فکنم گرد سرایت", "tr": "Ay yüzlü sevgilim, bilmiyorsun ki her gece gide gele sarayının etrafına hâle gibi yol yaptım."}, {"fa": "تا تیر تو بر من در صد ذوق گشادست\nهر زخم دهانیست مرا بهر دعایت", "tr": "Okların vücudumuzda yüzlerce zevk kapısı açalı beri her yara sana dua eden bir ağız olmuştur."}, {"fa": "تا نقش تو بر لوح دل و دیده کشیدیم\nقطعا نکشیدیم سر از تیغ جفایت", "tr": "Gönül ve göz sayfasına resmini nakşedeli beri asla senin cefa kılıcından başımızı korumadık (çekmedik)."}, {"fa": "کس نیست که اندیشه زلف تو ندارد\nتنها نه فضولیست گرفتار بلایت", "tr": "Senin belana tutulan yalnız Fuzuli değildir. Kim var ki saçlarına meftun olmamış olsun. GAZEL NO: 72"}, {"fa": "باغبان لطف قد آن سرو در شمشاد نیست\nکی نماید تربیت جایی که استعداد نیست", "tr": "Ey bahçıvan, o servinin endamındaki letafet şimşad (şimşir) ağacında yoktur. Kendinde istidat olmadıktan sonra istediğin kadar özenerek yetiştir."}, {"fa": "گر خط دور لبت را بر زبان آرم مرنج\nنقش شیرین را ضرر از تیشه فرهاد نیست", "tr": "Dudağının etrafındaki ayva tüylerinin güzelliğinden bahsedersem (ağzıma alırsam) incinme. Ferhad’ın kazmasından Şirin’in resmine bir zarar gelmez."}, {"fa": "ساغر خونابه دل بسته ره بر ناله ام\nمست بزم حیرتت را رخصت فریاد نیست", "tr": "Gönül kanının kadehi feryadımın yolunu kesmiştir (bağlamıştır). Senin hayretinin meclisinde feryada ruhsat yoktur."}, {"fa": "کی شود واقف ز ادراک عذاب آخرت\nآنکه در دنیا به بیداد بتان معتاد نیست", "tr": "Dünyada güzellerin (putların) cefasına alışmayan âhiret azabını nasıl idrak edebilir."}, {"fa": "زاهدان را نیست منع عشق اندک محنتی\nهیچ کس از محنت قید جهان آزاد نیست", "tr": "Zâhitler aşkı men için az mihnet çekmiyorlar; cihan kaydının mihnetinden hiç kimse kurtulamıyor."}, {"fa": "رغبت نزهتگه میخانه از زاهد مجو\nجغد را در طبع میل منزل آباد نیست", "tr": "Zâhit, yürekler açan meyhaneye rağbet arama! Baykuşun tıynetinde mamur yerlere meyl ü muhabbet yoktur."}, {"fa": "کرده ای تدبیر ترکِ مِی ، فضولی فکر کن\nگر ز عقل است این ، بنایِ عقل را بنیاد نیست", "tr": "Fuzuli, şarabı terk etmeyi kararlaştırmışsın. Düşün bak, eğer bunu sana aklın emrediyorsa akıl binasının temeli yoktur (esassızdır)."}, {"fa": "تن که از تیر تو چون زنجیر روزن روزنست\nتا شدم دیوانه عشق تو زنجیر من است", "tr": "Aşkının yüzünden deli oldum olalı oklarınla zincir gibi göz göz olan vücudum benim zincirimdir."}, {"fa": "جان برون از تن باستقبال تیرت رفت و نیست\nغیر پیکانت کنون جانی که ما را در تن است", "tr": "Can senin okunu karşılamak için vücudumdan çıkıp gitti. Şimdi vücudumuzda senin okunun temreninden başka can yoktur."}, {"fa": "شاکرم دور از گل رویت ز چشم خون فشان\nمنزلم از قطرهای خون او چون گلشن است", "tr": "Senin gül yanağından ayrı düştüğüm zaman kanlı yaşlar döken gözüm beni teselli ediyor. Ona minnettarım. Zira döktüğü kan damlaları evimi gülşene çeviriyor."}, {"fa": "ماه من بی مهر رخسارت چگویم حال خود\nظلمتی کز هجر دارد روزگارم روشن است", "tr": "Ay yüzlü sevgilim, güneş kadar parlak yanağından ayrı düştüğüm zaman ne hallere giriyorum, söylemeye hacet var mı? Ayrılık yüzünden hayatımın ne kadar karanlık geçtiği sana aydındır (aşikârdır)."}, {"fa": "نیست در عشق توام جز جان سپردن چاره\nشمع اگر خواهد نجات از سوختن در مردن است", "tr": "Aşkının ıstırabından kurtulmak için tek çare can vermektir. Mum yanmaktan kurtulmak isterse yegâne çaresi sönmektir (ölmektir)."}, {"fa": "بی قراری راست ره در کویت ای ابرو کمان\nکش بسان تیر پا بهر تردد زاهن است", "tr": "Ey yay kaşlı sevgilim, senin diyarına ancak gidip gelmek için ok gibi ayağı demirden olan bir bikarar yol bulabilir."}, {"fa": "دوست می دارد ترا هر کس که باشد در جهان\nبر فضولی رحم کن کاو را جهانی دشمن است", "tr": "Cihanda kim varsa seni seviyor. Fuzuli’ye acı; zira ona bütün bir cihan düşman kesilmiştir."}, {"fa": "در هجر یار حال دل زار مشکل است\nزین حال واقف ار نشود یار مشکل است", "tr": "Yârdan ayrı düşüp gözyaşları döken bir gönlün hâli müşküldür. Fakat bu hâli sevgili bilmezse asıl müşkül odur."}, {"fa": "آسان بوصل یار رسیدن توان ولی\nدر وصل یار دیدن اغیار مشکل است", "tr": "Yârın visaline ermek kolaydır ama yârın visali anında ağyar görmek müşküldür."}, {"fa": "طعن است بر من از همه سو کار دشمنان\nگر دوست چاره نکند کار مشکل است", "tr": "Düşmanların işi bana her taraftan hücum etmek, bana hakaret etmektir. Eğer sevgili buna bir çare bulmazsa iş müşküldür."}, {"fa": "بسیار شد غم از کمی التفات یار\nکم نیست این معامله بسیار مشکل است", "tr": "Yâr bana az iltifat ettiği için çok muztaribim. Bu işi kolay sanma, çok müşküldür (az müşkül sanma, çok müşküldür)."}, {"fa": "از گرد راهت ار نرسد دم بدم مدد\nتسکین آب دیده خونبار مشکل است", "tr": "Eğer yolunun tozundan her an imdat erişmezse kanlı gözyaşı döken gözümün suyunu dindirmek müşküldür."}, {"fa": "زاهد نمی شود نکند منع ما ز می\nمعتاد را تغییر اطوار مشکل است", "tr": "Zâhit şarabı men etmedikçe rahat edemiyor. İnsan itiyadını güç değiştirir (alışmış kudurmuştan beterdir)."}, {"fa": "رفتن فضولی از سر آن کوی سهل نیست\nدوری عندلیب ز گلزار مشکل است", "tr": "Fuzuli, sevgilinin diyarını bırakıp gitmek kolay değildir. Bülbülün gülistandan uzaklaşması müşküldür."}, {"fa": "مه من شام غمت را سحری پیدا نیست\nآه ازین غم که ز مهرت اثری پیدا نیست", "tr": "Ay yüzlü sevgilim, senin aşkının ıstırabı tükenmek bilmiyor. Bu ıstırap gecesinin sabahı yok. Nasıl feryat etmeyeyim, nasıl ıstırap çekmeyeyim; sende sevgiden bir eser görünmüyor."}, {"fa": "بر تو گر من نگزینم دگری نیست عجب\nچه کنم در همه عالم دگری پیدا نیست", "tr": "Bir başkasını sana üstün göremiyorsam buna şaşma. Ne yapayım, bütün âleme bakıyorum, senden başkası yok ki."}, {"fa": "دل شد آواره و زد عشق تو آتش در تن\nخانه ام سوخت ، ز آتش شرری پیدا نیست", "tr": "Aşkın vücuduma ateş verdi; gönül yersiz yurtsuz, avare kaldı. Koca bir ev ateşten yandı da onda bir kıvılcım gözükmüyor."}, {"fa": "غالبا سیل غمت برد ز جا دلها را\nکز دل گم شده ما خبری پیدا نیست", "tr": "Senin sevginin ıstırabı galiba gönülleri bir sel gibi silip süpürdü. Onun için bizim kaybolan gönlümüzden hiçbir haber yok."}, {"fa": "گم شدم در صف عشاق نپرسید آن مه\nکه درین دایره خونین جگری پیدا نیست", "tr": "Âşıkların safından çekildim de o ay yüzlü sevgilim, bu dairede bir ciğeri kan olmuş zavallı vardı, ne oldu? diye sormadı bile."}, {"fa": "اشک را خوار مبینید که در بحر وجود\nطلبیدیم ازین به گهری پیدا نیست", "tr": "Gözyaşını hakir görmeyiniz. Zira aradık taradık bu varlık denizinde ondan daha iyi bir inci yok!"}, {"fa": "در ره عشق فضولی دم رسوایی زد\nچند گویید که صاحب نظری پیدا نیست", "tr": "Aşk yolunda rüsvâlıktan işte Fuzuli dem vurdu. Daha ne kadar “ortada bir nazar sahibi yok” diyeceksiniz."}, {"fa": "ای طربخانه دل خلوت سلطان غمت\nپرده دیده سراپرده خاک قدمت", "tr": "Ey sevgilim, gönül zevk ü safâ evi, senin aşkının ıstırabının sultanına halvet odasıdır; gözümün perdesi, ayağının toprağının seraperdesidir."}, {"fa": "وعده داد مرا ماه من امشب ای صبح\nبخدا گر همه صدقست نگه داردمت", "tr": "Ay yüzlü sevgilim, bu gece bana söz verdi. Allah aşkına ey sabah, nefesin tamamen doğruluk da olsa (subh-ı sâdık), nefesini tut."}, {"fa": "بعلاجی دگرم حال مگردان که مرا\nساز کارست بسی شربت ذوق المت", "tr": "Başka bir ilaç ile beni altüst etme; zira senin ıstırabının zevkini bir şerbet (ilaç) gibi içiyor, şifa buluyorum."}, {"fa": "سزد ار دم زند از سلطنت روی زمین\nخاکساری که بود گرد حریم حرمت", "tr": "Senin hareminin içinde toz olan bir hakir (toprak gibi), yeryüzüne suhanım dese değer."}, {"fa": "نفسی نیست که در رشته جانم گرهی\nنفتد از شکن سلسله خم بخمت", "tr": "Bir nefes, bir an yoktur ki kıvrım kıvrım saçının zincirinin bükümünden canımın ipine bir ukde (düğüm) düşmesin."}, {"fa": "گریه در دیده ز بیداد تو آبی نگذاشت\nعذر خواه کمی ماست کمال کرمت", "tr": "O kadar zulmettin ki ağlaya ağlaya gözümüzde yaş tükendi. Kereminin kemali bizim bu kusurumuzu mazur görür."}, {"fa": "می کند منع تو از قتل فضولی اغیار\nاین چه ظلم است بران کشته تیغ ستمت", "tr": "Seni ağyâr, Fuzuli’yi öldürmekten men‘ ediyor. Zulmünün kılıcı ile zaten ölmüş olan o zavallıya bu ne zulümdür?"}, {"fa": "هر زمان حال من از عشق تو دیگرگون است\nبتو چون شرح کنم حال چه گویم چون است", "tr": "Senin sevgin beni her an türlü hâllere sokuyor. Sana “hâlim budur” diye nasıl söyleyeyim? (Hâlimi sana nasıl anlatayım.)"}, {"fa": "غم دل سوخت مرا پیش که آرم به زبان\nقصه درد درونم که ز حد بیرون است", "tr": "Gönül derdi beni yaktı. Bu hadden aşan ıstırabı kime anlatayım?"}, {"fa": "شده از ناوک آهم دل گردون مجروح\nرنگ خون است شفق نیست که بر گردون است", "tr": "Âhımın oku feleğin yüreğini yaraladı. O gökte gördüğün şafak değil, kandır (kan rengidir)."}, {"fa": "لب شیرین ترا چون ورق گل خوانم\nکان صحیفه نه بدین خط و بدین مضمون است", "tr": "Senin tatlı dudağına nasıl gül yaprağı (sahife) derim (okurum)? Çünkü o sahifede ne bu ayva tüyleri (yazı) ne de bu mana var."}, {"fa": "دل دمی نیست که یاد لب لعلت نکند\nگریه دیده پر خون ز دل محزون است", "tr": "Bir nefes (dem) yoktur ki gönül lâ‘l dudağını anmasın. Kanlı yaşlar döken gözümün ağlaması mahzun gönlümdendir (yaşlar oradan geliyor)."}, {"fa": "چه غم از هجر گرت هست کمالی در عشق\nرخ لیلی همه جا در نظر مجنون است", "tr": "Eğer aşkta kemal sahibi isen hicrandan endişe etme. Her nerede olursa olsun Leylâ’nın didarı Mecnun’un gözü önündedir."}, {"fa": "خواب را کشت فضولی غم او در دیده\nاین که بر چهره ام از دیده روان شد خون است", "tr": "Fuzuli, onun aşkının ıstırabı gözünde uykuyu öldürdü. Gözümden yüzüme bu akan onun kanıdır."}, {"fa": "شده ام بسته گیسوی شکن پر شکنت\nمکش ای گل که بگردن نفتد خون منت", "tr": "Senin kıvrım kıvrım saçlarına bağlanmışım. Ey gül, beni öldürme; kanım boynuna düşmesin."}, {"fa": "غایت لطف تن از چشم منت کرد نهان\nاین چه جورست که من می کشم از لطف تنت", "tr": "Tenin o derece latif maddeden, o derece âri, esirdir ki gözümden seni nihan etti. Teninin bu letafetinden ne cefalar çekiyorum."}, {"fa": "خاک گشتم که مرا سایه ات افتد بر سر\nکرد نومیدم ازان نیز صفای بدنت", "tr": "Başıma gölgen düşer diye toprak oldum. Bedeninin son derece saflığı beni bundan da nevmit etti."}, {"fa": "تو بگفتار در آور نه بقول دگران\nهیچ راهی نتوان برد بسر دهنت", "tr": "Sen bir söz söyle; yoksa başkalarının sözüyle ağzımın sırrına ermek kabil değildir."}, {"fa": "لب میگون تو دارد سر خون ریختنم\nهمه دم می شود این فهم ز رنگ سخنت", "tr": "Şarap renkli dudağın kan mı dökmek sevdasında? Her an (dem) sözünün renginden bu anlaşılıyor."}, {"fa": "چند سازد رسن از رشته جان دلو ز دل\nمردم دیده کشد آب ز چاه ذقنت", "tr": "Gözbebeği ne zamana kadar can riştesinden ip, gönülden kova yapıp çenenin kuyusundan su çekecek?"}, {"fa": "آتشی هست چو فانوس فضولی در تو\nنیست خون اوست نمایان شده از پیرهنت", "tr": "Fânus gibi senin içinde de ateş var, Fuzuli. Gömleğinden görünen kan değil, o ateştir."}, {"fa": "بتی که شیوه خوبی به از تو داند نیست\nپری وشی که ز دست توام رهاند نیست", "tr": "Sen bir güzelsin ki (putsın ki) güzellik edasını senden daha iyi bilen yoktur. Beni senin elinden kurtaracak bir peri yoktur."}, {"fa": "هزار نامه نوشتم بیار لیک چه سود\nکسی که لطف نماید باو رساند نیست", "tr": "Sevgilime binlerce mektup yazdım. Fakat ne faide, lütfedip bunları ona götürecek kimse yok."}, {"fa": "دهد بدست تو هرکس که هست نقد حیات\nولی کسی که ز تو کام دل ستاند نیست", "tr": "Herkes hayat nakdini senin eline verir; fakat kimse senden gönlünün muradını alamaz."}, {"fa": "بلوح دهر حدیث گذشتگان یک یک\nنوشته اند ولی عارفی که خواند نیست", "tr": "Dünya sahifesine gelip gidenlerin macerası birer birer yazılmıştır. Fakat onu okuyan bir ârif yok!"}, {"fa": "همه اسیر غم عالیم راه روی\nکه رخش همت ازین تنگنا جهاند نیست", "tr": "Hepimiz dünya gamına esiriz. Bir yolcu yok ki himmet atını bu dar yoldan sıçratıp kurtulsun."}, {"fa": "بمی چه میل کنم آزموده ام آن هم\nچنانکه سوز غم عشق را نشاند نیست", "tr": "Şaraba ne meyledeyim? Onu da tecrübe etmişim; aşk ıstırabının ateşini bir şey söndüremiyor."}, {"fa": "بملک دهر فضولی مبند دل کانجا\nبسیست آمده اما کسی که ماند نیست", "tr": "Fuzuli, dünya mülküne gönül bağlama. Zira buraya gelen çok ama kalan hiç yok."}, {"fa": "گل بباغ آمد ولی از عمر خود کامی نیافت\nخارها در زیر پهلو داشت آرامی نیافت", "tr": "Gül bağa geldi, fakat hayatından kâm alamadı. Kaburgalarının altındaki sayısız dikenler ona hiç rahat vermediler."}, {"fa": "کرد بلبل پیش گل بنیاد درد دل بسی\nزود گل بگذشت و آن درد دل اتمامی نیافت", "tr": "Bülbül, gülün huzurunda gönlünün derdini dökmeye başladı; fakat gül o kadar çabuk geldi geçti ki sözünü tamamlayamadı."}, {"fa": "وه چه ملکست این که دور گل گذشت کس درو\nرونق بزمی ندید و نشاه جامی نیافت", "tr": "Bu nasıl bir âlemdir (mülktür ki) bir gül devri geçti de kimse bu mevsim içinde bir zevk ü işret meclisinin zevkini, revnakını süremedi; bir kadehin neş’esini tadamadı."}, {"fa": "دوخت دوران از لطافت خلعتی اما چه سود\nقابل تشریف این خلعت گل اندامی نیافت", "tr": "Devran, letafetten bir hil‘at (elbise) dikti; fakat ne faide ki bu hil‘ati giyebilecek bir gül-endam bulamadı."}, {"fa": "گل که دیر آمد چرا زین باغ رحلت کرد زود\nغالبا چون من ز کس اعزاز و اکرامی نیافت", "tr": "Bağa gelirken geciken gül gene çabuk geçip gitti. Galiba benim gibi o da kimseden izzet ü ikram görmedi."}, {"fa": "چون ننالیم از جفای گردش گردون دون\nهیچ کار ما ز دور او سرانجامی نیافت", "tr": "Bu alçak dünyanın cefasından nasıl ağlayıp inlemeyelim. Onun devrinde hiçbir işimiz başarılmadı ki…"}, {"fa": "پایه قدر سخن دانان فضولی پست شد\nزین سبب هرگز درین کشور کسی نامی نیافت", "tr": "Fuzuli, söz bilenlerin (şairlerin) kıymeti düştü. Bu sebepten bu memlekette kimse reşitip şöhret sahibi olmadı."}, {"fa": "گر نقابی نبود مهر رخش را غم نیست\nتاب رخساره او هم ز نقابی کم نیست", "tr": "Güneş gibi parlak yüzü eğer örtülü değilse zarar yok. Yanağının parlaklığı da nikabdan aşağı değildir."}, {"fa": "نیست معلوم غم من همه عالم را\nهمچو من غمزده در همه عالم نیست", "tr": "Aşk yüzünden benim neler çektiğimi bütün âlem bilir. Bütün âlemde benim gibi bu derde düşmüş insan az değildir."}, {"fa": "می کند سجده بخاک سر کوی تو ملک\nهر که خاک سر کویت نبود آدم نیست", "tr": "Senin diyarının toprağına melek secde eder. Kim o diyarın toprağı olmamışsa adam değildir."}, {"fa": "عقل را کرد برون عشق تو از خانه دل\nکین سراسیمه بهمرازی من محرم نیست", "tr": "Senin aşkın, “bu sersem benimle sırdaş olamaz” diye aklı gönül evinden defetti."}, {"fa": "هر خم زلف تو جای دل سودازده ایست\nنیست یک دل که دران سلسله پرخم نیست", "tr": "Zülfünün her kıvrımında bir sevdazedenin gönlü vardır. Bir gönül yoktur ki o kıvrım kıvrım saçın içinde olmasın."}, {"fa": "هیچ کس را خبری نیست ز ذوق غم تو\nسبب اینست که دردهر دلی خرم نیست", "tr": "Senin aşkının ıstırabı ne zevklidir, kimse bilmez. Zira dünyada bir tek bahtiyar gönül yok ki!"}, {"fa": "مگذران بی می و معشوق فضولی زنهار\nحاصل عمر گرانمایه که جز یکدم نیست", "tr": "Fuzuli, bu değerli ömür topu topu ancak bir nefes sürer sürmez. Sakın bunu şarap ve sevgilisiz geçirme."}, {"fa": "بگل خطت چو نقابی ز مشک ناب انداخت\nهزار شاهد فتنه ز رخ نقاب انداخت", "tr": "Yüzündeki ayva tüyleri, gül üzerine saf miskten bir örtü atar atmaz binlerce fitne güzeli yüzlerinden örtüleri sıyırdılar."}, {"fa": "مه رخ تو که سر زد خط از خواشی آن\nهزار ناوک طعنه بر آفتاب انداخت", "tr": "Etrafında ayva tüyleri beliren o ay gibi yüzün, güneşe binlerce ta‘n, hakaret oku attı (onu hiç beğenmedi)."}, {"fa": "دمید تا خط چون شب ز روی چون روزت\nزمانه دیده بخت مرا بخواب انداخت", "tr": "Gündüze benzeyen yüzünde gece gibi ayva tüyleri belirince zaman benim bahtımı uykuya daldırdı (bedbaht etti)."}, {"fa": "بمردن از غم دل رسته بودم آن لب لعل\nحیات داد مرا باز در عذاب انداخت", "tr": "Ölüm sayesinde gönül derdinden kurtulmuştum. O lâ‘l dudak beni diriltip tekrar azaba mahkûm etti."}, {"fa": "هوای چین جبینت هزار موج بلا\nبآب دیده نم دیده پر آب انداخت", "tr": "Alnındaki naz kırışıklarının sevdası (havası), ağlayan gözümün suyu üzerinde binlerce bela dalgalandı."}, {"fa": "ز رشک بر دل خون گشته سوختم صد داغ\nچو خالهای لبش عکس در شراب انداخت", "tr": "Dudağındaki benlerin aksi şaraba düşünce o kadar kıskandım ki kan olmuş yüreğime hasetten yüzlerce dağ yaktım."}, {"fa": "چه ممکن است ثبات از فضولی بی دل\nچنین که شوق تو او را در اضطراب انداخت", "tr": "Senin sevgin onu böyle ıstıraba (çırpınmaya) attıktan sonra âşık Fuzuli’nin buna sabretmesi kabil midir?"}, {"fa": "از جان بدود دل غم خالت برون نرفت\nوز دیده این سواد بسیلاب خون نرفت", "tr": "Güzel beninin sevgisi yüreğimi yakıyor da onun dumanı ile canımdan çıkıp gitmiyor. Gözümdeki bu kara da kan seli ile silinip mahvolmuyor."}, {"fa": "از چاک سینه ام بدرون سر نهاد اشک\nوز سینه ام حرارت سوز درون نرفت", "tr": "Gözyaşı, göğsümün yarıklarından fışkırıp taştı da hâlâ içimin yanması dinmedi."}, {"fa": "از حسن الفتیست که گر رفت کوهکن\nهرگز خیال او ز دل بیستون نرفت", "tr": "O kadar güzel uyuşmuşlardı ki Ferhat öldü de hâlâ onun hayali Bîsütûn’un gönlünden silinmedi."}, {"fa": "آورد تاب جسم نزارم بآه دل\nدر حیرتم ز خاک که بر باد چون نرفت", "tr": "Bu zayıf cismim gönlümden yükselen âhlara tahammül etti. Hayretteyim; şu toprak bu rüzgâra nasıl dayandı (rüzgâra savrulmadı)."}, {"fa": "ساقی مرا علاج دگر کن که گرد درد\nز آیینه دلم بمی لاله گون نرفت", "tr": "Saki, bana başka bir ilaç ver. Zira renkli şarap gönlümün âyinesindeki dert tozunu gideremedi."}, {"fa": "صد دور کرد چرخ ولیکن بهیچ دور\nتاب بلا ز رشته بخت زبون نرفت", "tr": "Çarh yüzlerce defa döndü; fakat hiçbir dönüşü bu zebun talihimin ipindeki bela bükümünü açmadı (talihimin düğümünü çözmedi)."}, {"fa": "در راه عشق کرد فضولی وداع دل\nعاقل کسیست کز پی اهل جنون نرفت", "tr": "Aşk yolunda Fuzuli gönlüne veda etti. Akıllı adam odur ki delilerin arkasından gitmez."}, {"fa": "برگ گل کز هر طرف آرایش دستار تست\nجسته هر جانب شرار آتش رخسار تست", "tr": "Başörtünü her taraftan süsleyen gül yaprakları, yanağının ateşinden etrafa sıçrayan kıvılcımlardır."}, {"fa": "گر ترا حسن رخ از گلها فزاید دور نیست\nدر حقیقت گل تویی گلهای دیگر خار تست", "tr": "Güller yanağının güzelliğini artırıyorsa bu tabiidir. Çünkü hakikatte gül sensin; öteki güller senin dikenlerindir."}, {"fa": "غیرت رنگ رخت گل را گریبان کرد چاک\nغنچه خونین دل ز رشک درج گوهربار تست", "tr": "Yüzünün rengini gören güller hasetlerinden yakalarını parçaladılar. Goncaların bağrı senin inci saçan hokkanın (ağzının) hasedinden hun oldu."}, {"fa": "عشوه و رعنایی گل نیست در دل کارگر\nگل چه می داند چه باید کرد اینها کار تست", "tr": "Gülün işvesi, güzelliği insanın gönlüne tesir etmez. Gül, gönülleri tesir etmek için yapılması lâzım gelen şeyleri nereden bilecek; bunlar hep senin işin."}, {"fa": "آمد و بگذشت گل ما را نشد پروای آن\nما کجا و غیر تو ما را غرض دیدار تست", "tr": "Güller açtı, soldu, biz hiç onunla alâkadar olmadık. Biz hiç senden başkasıyla alâkadar olur muyuz? Biz senin dirliğini isteriz."}, {"fa": "نیست در سرو و صنوبر رسم و راه دلبری\nاین روش مخصوص بر شمشاد خوش رفتار تست", "tr": "Servide, sanavberde güzellik denen şey yoktur (güzelliğin usul ve tarzı). Bu tarz (gidiş) senin güzel yürüyüşlü şimşadına mahsustur."}, {"fa": "نیست گفتارت فضولی بی مذاق عاشقی\nزین سبب هرکس که دیدم عاشق گفتار تست", "tr": "Fuzuli, sözünde âşıklık çeşnisi var. Bu sebepten kimi gördümse hepsi senin sözüne âşıktır."}, {"fa": "آزمودم عشق خوبان را بلایی بوده است\nوانکه می گویند عاشق مبتلایی بوده است", "tr": "Tecrübe ettim, gördüm ki güzelleri sevmek bir bela imiş. Âşık dedikleri adam da bir müpteladan başka bir şey değilmiş."}, {"fa": "تا شدم عاشق عذابی می کشم چون بت پرست\nمیل چین زلف محبوبان خطایی بوده است", "tr": "Âşık olalı beri bir putperest gibi azıp çekmekteyim. Güzellerin zülfü’nün kıvrımına (çîn) meyletmek bir batıl imiş."}, {"fa": "نقش خویش و صورت شیرین کشیده کوهکن\nعاشق صورت پرست خودنمایی بوده است", "tr": "Ferhad, kendi resmi ile Şirin’in resmini yapmış; o surete tapan ve kendini göstermek isteyen bir âşık imiş."}, {"fa": "چار سوی دهر جای خودفروشانست و بس\nجوهر عرفان متاع ناروایی بوده است", "tr": "Dünya çarşısı mağrur (kendini satan) insanların yeri imiş. İrfan cevheri de bu pazarda geçmez bir meta imiş."}, {"fa": "بسته بر محراب دل اهل ورع قندیل وش\nگوشه مسجد عجب دلگیر جایی بوده است", "tr": "Zühd ü takvâ sahipleri kimdi ki mihraba gönül bağlamışlar… Bu mescit köşesi de ne kadar can sıkıcı (gönül tutucu) yer imiş."}, {"fa": "عشق بازی را سرور سینه می پنداشتم\nمحنت بی حد و درد بی دوایی بوده است", "tr": "Âşıklığı gönüle zevk ü sürur veren bir şey sanıyordum. Meğer sonsuz bir mihnet, devasız bir dert imiş."}, {"fa": "کم نشد از من فضولی محنت عالم دمی\nعرصه عالم عجب محنت سرایی بوده است", "tr": "[Tercüme Eksik]"}, {"fa": "ازان درین چمنم میل گلعذاری نیست\nکه هیچ برگ گلی بی بلای خاری نیست", "tr": "Bu çimende hiçbir gül yanaklıya meylim yok. Çünkü bir gül yaprağı yok ki yanı başında bir diken belası bulunmasın."}, {"fa": "نبرده ایم درین باغ ره بسوی گلی\nکه در حوالی او همچو من هزاری نیست", "tr": "Bu bahçede hiçbir bût’a erişmedik ki etrafında benim gibi bir bülbül (benim gibi bin nâle) bulunmasın."}, {"fa": "ندیده ایم درین ملک گنج حسنی را که\nکه از صف رقبا گرد او حصاری نیست", "tr": "Bu mülkte hiçbir güzellik hazinesi görmedik ki rakiplerin safı etrafına bir kale çevirmiş olmasın."}, {"fa": "ازان چمن چه گشاید که عندلیبان را\nدرو بسوی گل از خار رهگذاری نیست", "tr": "Güle varmak isteyen bülbüllerin dikenden yol bulamadıkları o çimende ne safâ vardır?"}, {"fa": "درین نشیمن حرمان هزار غم دارم\nفزون تر از همه این غم که غمگساری نیست", "tr": "Yârın ayrılığı beni ağyâr cefasından kurtardı. Benim dünyamın iyisiyle kötüsüyle işim yok."}, {"fa": "درون سینه دل تنگم از گلی نشگفت\nچگونه گل شکفد باغ را بهاری نیست", "tr": "Bu mahrumiyet yerinde binlerce gamım var. Bir dert ortağım yok. İşte bu dert, o binlerce dertten daha üstün."}, {"fa": "ز هیچ یار فضولی ندیده ایم وفا\nخوشا کسی که مقید بهیچ کاری نیست", "tr": "Sinemin içinde bu muztarip dar gönlüm bir gülün zevkiyle açılmadı. Baharı olmayan bahçede nasıl gül açsın."}, {"fa": "غیر ناکامی ز محبوبان مرا مطلوب نیست\nعاشقان را کام دل جستن ز خوبان خوب نیست", "tr": "Sevgililerden benim istediğim nâkâmlıktan (muradına erişmemekten) başka bir şey değildir. Âşıkların güzellerden gönül muradı aramaları güzel bir şey değildir."}, {"fa": "چون ندیدم صد جفا از یار می خواهم وفا\nچیزی از محبوب می خواهم که در محبوب نیست", "tr": "Nasıl yüzlerce cefa görmeyeyim; yârdan vefa istiyorum. Sevgiliden öyle bir şey istiyorum ki o kendisinde yoktur. Elbette cefa eder."}, {"fa": "مرد باید تا نیازارد ز خود معشوق را\nبهر یوسف در زلیخا رأفت یعقوب نیست", "tr": "Mert lâzımdır ki sevgili kendisinden incinmesin. Yusuf’a karşı Züleyha’nın kalbinde Yakub’un şefkati yoktur."}, {"fa": "شد دلم صد پاره ناوردم شکایت بر زبان\nمحنت و صبری که در من هست در ایوب نیست", "tr": "Gönlüm yüz parça oldu, ağzımı açıp bir şikâyet etmedim. Bende olan sabır Eyyûb peygamberde yoktur."}, {"fa": "نیست جز لیلی بقای عشق مجنون را سبب\nضایع است آنکس که بر گل چهره منسوب نیست", "tr": "Mecnun’un aşk hikâyesinin böyle asırlarca sürüp gitmesine sebep Leylâ’dır. Bir gül yanaklı güzele mensup olmayan insan mahvolmuş demektir; ne adı kalır, ne sanı."}, {"fa": "زاهد گچ رو ندارد رغبت عشق بتان\nراستی را این روش از هیچ کس مرغوب نیست", "tr": "Eğri yoldan yürüyen zâhit güzellerin (putların) aşkına rağbet etmiyor. Doğrusu bu gidişi (hareket) kimse beğenmez."}, {"fa": "نیستم یک دم فضولی بی تماشای بتان\nشاهد مقصد ز من در هیچ جا محجوب نیست", "tr": "Fuzuli, bir an güzelleri (putları) temaşadan hâli değilim. Gönlümün istediği güzel hiçbir yerde bana karşı örtülü değildir."}, {"fa": "سایه ات را متصل ذوق وصالت حاصل است\nنیست دور از دولتی اما چه حاصل غافل است", "tr": "Gölgen daima senin visalinle zevk bulmaktadır. O devlet ve saadetten uzak değil; ama ne fayda, bilmiyor."}, {"fa": "حل مشکل نیست مشکل پیش او اما چه سود\nمشکل خود پیش او اظهار کردن مشکل است", "tr": "Onun huzurunda müşkilini halletmek müşkil değildir; ama ne fayda ki onun huzurunda müşkilini açığa vurmak müşkildir."}, {"fa": "پا کشید از چشمه چشمم ز بیم فتنه خواب\nکین گذرگه مردم خونریز را سرمنزل است", "tr": "Uyku gözümün çeşmesine gelmez oldu (oradan ayağını çekti). Bu geçidin kan döken insanlara (merdüm) konak olduğunu görüp bir fitne, bir felaketten korkuyor."}, {"fa": "گر نماند رازم از غیر تو پنهان دور نیست\nهست دل پیش تو و رازی که دارم در دل است", "tr": "Eğer sırrım senden başkasına gizli kalmıyorsa bu tabiidir. Zira gönlüm senin yanındadır; sırrım da onun içindedir."}, {"fa": "ساده افتادست لوح خوبی از نقش وفا\nکام دل جستن ز محبوبان خیال باطل است", "tr": "Güzellik sayfasında vefa yazısı yoktur. Onun için sevgililerden murat istemek batıl bir hayaldir."}, {"fa": "سر بگردون گر کشد از روی رفعت دور نیست\nهر کرا چون سبزه در کوی بلا پا در گل است", "tr": "Bela diyarında ot gibi kimin ayağı çamurda ise (felâkette) onun yükselip başını göklere değdirmesi gayet tabiidir."}, {"fa": "آفت تکلیف را ره نیست در ملک جنون\nکی رود بیرون ازین کشور فضولی عاقل است", "tr": "Cünun mülkünde herkes hürdür. Kimse bir şey ile mükellef değildir. Fuzuli akıllıdır; hiç bu memleketten dışarı çıkar mı?"}, {"fa": "دل الفت تمام بآن خاک در گرفت\nخوش صحبتی میان دو افتاد در گرفت", "tr": "Gönül, onun kapısının toprağı ile mükemmel surette uyuştu ve anlaştı. İki düşkün arasında güzel bir arkadaşlık teşessüs etti."}, {"fa": "خونابه نیست بر مژه ام آتش دل است\nکز چاک سینه سر زد و در چشم تر گرفت", "tr": "Kirpiğimde gördüğün kanlı gözyaşı değildir; göğsümün yarığından fışkırıp yaşlı gözümde yedeşen gönül ateşidir."}, {"fa": "چون من بسیست باده کش بزم عشق لیک\nبودم تنک شراب مرا بیشتر گرفت", "tr": "Benim gibi aşk bezminde şarap içen çoktur; lâkin biraz efkârlı idim, beni fazla tuttu."}, {"fa": "چون شمع باز در سرم افتاد گرمی\nدل کرده بود ترک تعلق ز سر گرفت", "tr": "Mum gibi gene başım dumanlandı. Gönül bütün alâkalarından sıyrılmıştı; yeniden tutuldu."}, {"fa": "شوق حریم روضه کوی تو داشت گل\nبگشاد دست و دامن باد سحر گرفت", "tr": "Gül, senin diyarının cennetinin harimini hararetle istiyordu. Elini açtı ve seher rüzgârının eteğine yapıştı."}, {"fa": "فرهاد در زمانه من گشت کوهکن\nبگذاشت عاشقی پی کار دگر گرفت", "tr": "Ferhat, benim zamanımda dağ kazıcı oldu. Âşıklığı bırakıp başka bir iş tuttu."}, {"fa": "چون خس فتاده بود فضولی بخاک ره\nاو را نسیم لطف تو از خاک بر گرفت", "tr": "Fuzuli bir saman parçası gibi yollara düşmüştü; onu senin lutfunun rüzgârı topraktan kaldırdı."}, {"fa": "هوای خاک درت باز در سر افتادست\nز هر چه هست مرا این هوا در افتادست", "tr": "[Tercüme Eksik]"}, {"fa": "مرا چه کار به از آه و ناله است کنون\nکه کار با تو چو شوخ ستمگر افتادست", "tr": "[Tercüme Eksik]"}, {"fa": "چرا ز چشم تو بر من نمی افتد نظری\nمگر که قاعده مردمی در افتادست", "tr": "[Tercüme Eksik]"}, {"fa": "ز زلف یار صبا تا گشاده است گره\nگره بکار دل درد پرور افتادست", "tr": "[Tercüme Eksik]"}, {"fa": "من از کجا و نجات از بلا چنین که دلم\nبدام آن سر زلف معنبر افتادست", "tr": "[Tercüme Eksik]"}, {"fa": "ز ذوق عشق بتان نیست عقل را خبری\nچرا که رتبه این ذوق برتر افتادست", "tr": "[Tercüme Eksik]"}, {"fa": "فتاده است فضولی بخاک رهگذرت\nبیا که بی تو غریبی ببستر افتادست", "tr": "[Tercüme Eksik]"}, {"fa": "بر جان ما جفای نکویان ز حد گذشت\nاوقات ما میانه این قوم بد گذشت", "tr": "Güzellerin canımıza reva gördükleri cefa hadden aştı. Ömrümüz bu kötü kavim arasında geçti."}, {"fa": "سوز و گداز شمع ز رشک جمال تست\nرست از همه عذاب کسی کز حسد گذشت", "tr": "Mumun yanıp yakılması senin güzelliğine haset ettiğindendir. Hasetten vazgeçen insan her türlü azaptan kurtulur."}, {"fa": "نشمرد از سکان خودم هیچ دلبری\nبر من ز دلبران ستم بی عدد گذشت", "tr": "Hiçbir güzel beni köpekleri arasında saymadı. Şu güzeller bana sayısız zulmetmişlerdir."}, {"fa": "در خون نشست مردم چشمم ز آرزو\nهر گه که در خیال من آن خال و خد گذشت", "tr": "O yanağı her tahayyül ettikçe gözbebeğim hasretten kana batıyor."}, {"fa": "گشتم مقید غم عشق تو از ازل\nهرگز نمی توانم ازین تا ابد گذشت", "tr": "Senin aşkına ezelden bağlandım. Ondan ebediyen vazgeçemem."}, {"fa": "عمرم گذشت لیک ندارم تأسفی\nشادم باین که در غم آن سرو قد گذشت", "tr": "Ömrüm geçti lâkin esef etmiyorum. O servi boylu sevgilinin aşkı ve ıstırabı içinde geçtiği için seviniyorum."}, {"fa": "ذوق وصال او ز فضولی دریغ نیست\nاما بشرط آنکه تواند ز خود گذشت", "tr": "Onun visalinin zevki Fuzuli’den esirgenmiş değildir; ancak kendinden geçebilmek şartıyla."}, {"fa": "در دل لاله غمت آتش سودا انداخت\nشمع را آتش سودای تو از پا انداخت", "tr": "Aşkının ıstırabı gelinciğin gönlüne sevda ateşi saldı. Senin sevdanın ateşi şem‘i devirdi."}, {"fa": "یافت از نکهت زلف تو خبر آهوی چین\nنافه مشک خود از شرم بصحرا انداخت", "tr": "Çin ahusu zülfünün koku sundan haber alır almaz göbek miski utancından kendini sahralara attı."}, {"fa": "تا ز دیدار تو مانع نشود چشم پرآب\nخواب را در نظرم کشت بدریا انداخت", "tr": "Seni görmekten beni men etmesin diye yaşla dolu gözüm uykuyu öldürüp denize attı."}, {"fa": "رشک رخسار تو زد بتکده ها را بر هم\nآه من غلغله در گوش مسیحا انداخت", "tr": "Yanağının hasedi puthaneleri perişan etti. Benim âhım İsa’nın kulağına velvele saldı."}, {"fa": "برگشادی بسخن صد گرهم چون سبحه\nعقد دندان تو بر رشته تقوا انداخت", "tr": "Ağzını açıp söze başlar başlamaz inci dizisine benzeyen dişlerin benim zühd ü takva ipime tesbih gibi yüzlerce düğüm attı."}, {"fa": "خواست آزار خود از ناوک آهم گردون\nکه غم عشق توام بر دل شیدا انداخت", "tr": "Galiba felek âhımın okundan kurtulmak istedi de senin aşkının elemini deli gönlüme saldı."}, {"fa": "سرورا از نظر انداخت فضولی چون ما\nیک نظر هر که بر آن قامت رعنا انداخت", "tr": "Fuzûlî, bir kere o güzel endamı gören bizim gibi serve bakmaz oldu (servi gözünden düşürdü)."}, {"fa": "کم التفاتی خوبان بعاشقان ستم است\nزهی ستم که ترا با من التفات کم است", "tr": "Güzellerin âşıklara az iltifat etmeleri zulümdür. Fakat düşün, ne büyük zulümdür ki sen bana az iltifat ediyorsun."}, {"fa": "کی از بنفشه گشاید کجا بنافه کشد\nدلی که بسته آن گیسوان خم بخم است", "tr": "O kıvrım kıvrım saçlara bağlanan bir gönlü menekşe nasıl açar; göbek miski nasıl kendine cezbeder?"}, {"fa": "قدم خمیده چنان شد که کس نمی داند\nمرا براه تو پوینده فرق یا قدم است", "tr": "Belim öyle büküldü ki senin yolunda koşup duran başım mıdır, ayağım mıdır, kimse fark edemiyor."}, {"fa": "ز دام شوق دل ما نمی رهد مادام\nکه سنبل تو گذرگاه باد صبحدم است", "tr": "Senin sümbül saçın sabah rüzgârının geçtiği yol olduktan sonra gönlümüz sevgi tuzağından kurtulmaz."}, {"fa": "طبیب را الم من نماند تا ره برد\nبلذتی که مرا در ره تو از الم است", "tr": "Senin yolunda çektiğim ıstıraplardan ne kadar zevk duyduğumu anlayalı beri tabip, beni tedavi çarelerini aramak zahmetinden kurtuldu."}, {"fa": "چنین که زندگیم با غمست در عشقت\nمرا ازان که اجل قصد جان کند چه غم است", "tr": "Senin aşkının elemi içinde böyle kıvrandıktan sonra ecel canıma kast etmiş; ne korkum var?"}, {"fa": "ز رنج عشق فضولی فراغتی مطلب\nخموش باش که ذوق حیات مغتنم است", "tr": "Fuzûlî, aşk ıstırabından kurtulmak isteme. Sus; zira bu ele geçen hayat ganimetinin zevkidir."}, {"fa": "هست با خلعت گلگون قدت ای حور سرشت\nالفی کش قلم صنع به شنگرف نوشت", "tr": "Ey mayası hurilerden olan güzel, bu gül renkli elbise içinde senin endamın Tanrı’nın sanat kaleminin kırmızı has boya (şengref) ile yazdığı elife benziyor."}, {"fa": "جامه گلگون من آن طرفه نهالیست که دهر\nآبش از خون جگر داد از آن روز که کشت", "tr": "Benim gül renkli elbiseli sevgilim o güzel fidandır ki felek ektiği günden beri onu ciğer kanı ile sulamıştır."}, {"fa": "خلعت آل تو آن آتش ابراهیم است\nکه نهانست درو نزهت گل‌های بهشت", "tr": "Senin bu al elbisen içinde cennet güllerinin safası gizlenmiş İbrahim ateşidir."}, {"fa": "گلبن از گل‌بدن آراست تو با جامه آل\nظاهر است این که درین پرده که خوبست و که زشت", "tr": "Gül fidanı vücudunu gül ile süsledi; sen de al elbise ile. Bu perde altında kimin güzel, kimin çirkin olduğu aşikârdır."}, {"fa": "جامه آل تو می‌خواست بدوزد که فلک\nرشته جان من آغشت به خونابه و رشت", "tr": "Felek senin al elbiseni dokumak istediği zaman benim canımın ipliğini kana batırdı, büktü (de öyle dokudu)."}, {"fa": "هرکجا دامن گلرنگ کشیدی ای گل\nگشت برگ گلی از خون سرشکم هر خشت", "tr": "Ey gül, gül renkli eteğini nereye sürüyüp itti ise orada her taş (tuğla) parçası kanlı yaşımdan gül yaprağına döndü."}, {"fa": "همه‌دم میل فضولی به قبا گلگونیست\nچه کند آب و گلش دهر بدین رنگ سرشت", "tr": "Fuzûlî al elbiseli güzelleri sever. Ne yapsın, felek onun su ve çamurunu bu renk ile yoğurdu. GAZEL NO: 95"}, {"fa": "تا غایبی تو مجلس ما را حضور نیست\nدور از تو بی حضوری عشاق دور نیست", "tr": "Sen meclisimizde bulunmadıkça (gaip oldukça) bizim huzurumuz yoktur. Âşıkların senden uzakta bulundukça huzur yüzü görmemeleri garip görülmemelidir."}, {"fa": "رفتی و رفت تاب و توان از تن ضعیف\nما طاقت فراق نداریم زور نیست", "tr": "Sen gittin; bu zayıf âşıktan da tabii kuvvet gitti. Zorla değil ya, biz senin ayrılığına dayanamıyoruz."}, {"fa": "شبهای غم چو شمع دم صبح بی رخت\nدر دیده گرچه اشک روان هست نور نیست", "tr": "Senin didarından ayrı geçirdiğimiz gam geceleri, gözümüz sabahleyin söndürülmesi unutulmuş muma benziyor. Gözyaşı akıyor ama nuru yok."}, {"fa": "بی تو قرار یافتن و زیستن دمی\nبس مشکل است کار من ناصبور نیست", "tr": "Sensiz biraz sükûn, bir an yaşamak çok müşkül. Bu, ben sabırsızın işi değil."}, {"fa": "بخرام کز قد تو خدنگ بلا رسد\nبر هر دلی کز آمدنت پر سرور نیست", "tr": "Salındı, senin gelişinden sevinçle dolmayan her gönüle endamından oklar yağsın."}, {"fa": "گرد رهت برابر کحل است در نظر\nمی بیند این معاینه هر کس که کور نیست", "tr": "Senin yolunun tozu nazarımızda sürme ile beraberdir. Kör olmayan herkes bunu görür."}, {"fa": "شد ساکن در تو فضولی وزین سبب\nاو را هوای جنت و پروای حور نیست", "tr": "Fuzûlî senin kapında mesken edindi. Bunun için onda cennet sevgisi (havası), huri alâkası yoktur."}, {"fa": "سنگ بیداد بتان آیینه دل را شکست\nهر تمنایی که در دل داشتم صورت نبست", "tr": "Güzelierin (putların) zulüm taşı gönül aynasını kırdı. Gönlümde ne arzu varsa bir türlü hasıl olmadı (suret bulmadı)."}, {"fa": "وصل آن مه گر میسر نیست ما را دور نیست\nمقصدی داریم عالی همتی داریم پست", "tr": "O ay gibi güzelin visaline nail olamazsak bunu garip (uzak) bulmamalı. Çünkü maksadımız yüksek, fakat himmetimiz alçaktır."}, {"fa": "ز آتش دل بس که از هر زخم سر زد شعله\nسوخت چون تیرت دمی هر کس که پهلویم نشست", "tr": "Her yaramdan öyle gönül ateşi alevleri fışkırdı ki yanımda (kaburga kemiklerim) bir an (dem) oturan herkes, orada senin okun nasıl yandı ise öyle yandı."}, {"fa": "جوهر هستی متاع وصل جانان را بهاست\nما کجا و این هوس ما را که می گوید که هست", "tr": "Cananın visali metaı, paha varlık cevheridir. Biz nerede, o visale heves etmek nerede? Kim diyor ki bizde varlık var."}, {"fa": "جای جز در دل مکن معشوق را گر عاشقی\nسنگ دل باید که باشد هر که شد آتش پرست", "tr": "Eğer âşık isen gönülden başka bir yerde mâşukuna yer verme! Ateşe tapan insan taş yürekli olmalıdır."}, {"fa": "کام خواهی صبر کن پرویز وار ای کوهکن\nکی توان بر خورد از شیرین لبان با زور دست", "tr": "Ey Ferhat, murada ermek istiyorsan Perviz gibi sabret. Şirin dudaklarından zorla bazu ile kâm alınır mı?"}, {"fa": "دل که در عشقت امید وصل و بیم هجر داشت\nسوخت وز تشویش نیک و بد فضولی باز رست", "tr": "Senin aşkında visal ümidini, hicran korkusunu taşıyan gönül yanmış; ve Fuzûlî şu “iyidir, bu kötüdür” endişesinden kurtulmuştur. GAZEL NO: 97"}, {"fa": "ذوق وصلت یافت دل از ساقی و ساغر گذشت\nشد خلیل خلوت خلت ز ماه و خور گذشت", "tr": "[Tercüme Eksik]"}, {"fa": "آب چشمم راست طوف آستانت آرزو\nهر چه خواهد می تواند کرد چون از سر گذشت", "tr": "[Tercüme Eksik]"}, {"fa": "چون مسیحا می تواند پای بر گردون نهد\nهر که او را هر چه پیش آمد چو سوزن در گذشت", "tr": "[Tercüme Eksik]"}, {"fa": "بهتر از من کس نمی داند طریق عاشقی\nزانکه عمرم در ره خوبان سیمین بر گذشت", "tr": "[Tercüme Eksik]"}, {"fa": "اختر برگشته ام را سوخت آخر برق آه\nآه ازین محنت که برق آهم از اختر گذشت", "tr": "[Tercüme Eksik]"}, {"fa": "خوش دلم کز عشق من افتاد تقریب سخن\nهر کجا حرفی ازان ماه ملک منظر گذشت", "tr": "[Tercüme Eksik]"}, {"fa": "نیست در بغدادیان مطلق فضولی رأفتی\nحیف عمر من که بی‌حاصل درین کشور گذشت", "tr": "[Tercüme Eksik]"}, {"fa": "نی همین صد روزن از تیر تو بر جسم من است\nسایه ام را هم ازو صد داغ چون من بر تن است", "tr": "Senin okun yalnız benim cismimde yüzlerce pencere açmış değildir. Gölgemin vücudunda da bu yüzden yüzlerce yara açılmıştır."}, {"fa": "بی رخت از غیر می خواهم بدوزم دیده را\nاین که می بینی بچشمم نیست مژگان سوزن است", "tr": "Senin dirlarından ayrı bulundukça başkasını görmemesi için gözümü dikmek istiyorum. Gözümde bu gördüğün kirpik değil, iğnedir."}, {"fa": "چون نیندازم برون خود را ز پیراهن چو مار\nبر تنم ماریست هر تاری که در پیراهن است", "tr": "Nasıl yılan gibi gömlek değiştirmek istemem? Gömleğimdeki her iplik (târ) tenimin üzerinde bir yılandır (mâr)."}, {"fa": "سنبلت را باد اگر برداشت از رویت مرنج\nمزرع حسن رخت را خوشه چین خرمن است", "tr": "Rüzgâr sümbül saçını (sümbüle) yanağından kaldırdı ise müteessir olma. O, senin güzelliğin tarlasında bir başak toplayıcıdır."}, {"fa": "از مسیحا نیست کم ، در روح بخشی بویِ گل،\nغنچه بهر آن مسیحا مریم آبستن است", "tr": "Gül kokusu ruh vermekte İsa’dan aşağı değildir. Gonca o Mesih’i karnında taşıyan bir Meryem’dir."}, {"fa": "هست شاهد بر جفاهای زلیخای هوا\nیوسف گل را که چندین چاکها بر دامن است", "tr": "Gül Yusufunun eteği niçin bu kadar parçalanmıştır, bilir misin? Aşk (hava) Züleyha’sının cefalarını gördüğü için."}, {"fa": "سوخت از سر تا قدم خود را فضولی بهر دوست\nشمع بزم دوست شد او را چه باک از دشمن است", "tr": "Fuzûlî sevgilisi uğrunda baştan aşağı yanmıştır. O, sevgilisinin bezmine şem oldu. Artık düşmandan korkar mı?.."}, {"fa": "به حال زار من آن ماه را نگاهی نیست\nچه سود روزی اگر نیز هست ماهی نیست", "tr": "O ay yüzlü sevgilim, benim bu perişan hâlime dönüp bakmıyor. Ne faide; bir gün varsa bir ay yok."}, {"fa": "جفاست از تو همیشه مراد من نه وفا\nکه گاه گاهی اگر هست گاه گاهی نیست", "tr": "Ben senden vefa değil, daima cefa istiyorum. Cefan arasıra varsa da arasıra da yoktur."}, {"fa": "چگونه جانب مسجد روم به میخانه\nز کوی عشق به ملک صلاح راهی نیست", "tr": "Nasıl meyhaneden mescit tarafına giderim? Aşk diyarından salâh mülküne yol yoktur."}, {"fa": "ز جاه و منصب عالم فراغتی دارم\nبه جز فتادگی‌ام منصبی و جاهی نیست", "tr": "Âlemin mevki ve rütbesinden elimi eteğimi çekmişimdir. Düşkünlükten başka rütbe ve mevkiim yoktur."}, {"fa": "نکوترین رقم بر صحیفه هستی\nدرین که صورت خوب است اشتباهی نیست", "tr": "Varlık sahifesi üzerinde en güzel yazı, hiç şüphe yok ki güzel bir yüzdür."}, {"fa": "چه نخل گویمت ای گلبن حدیقه حسن\nکه حاصلم ز تو جز ناله و آهی نیست", "tr": "Ey güzellik bahçesinin gül fidanı, sana ne fidanı diyeyim; feryad ü âhdan başka bir mahsulünü aldığım yok ki!"}, {"fa": "ز ظلم پیش که نالد فضولی مسکین\nدرین دیار چو غیر از تو پادشاهی نیست", "tr": "Âciz Fuzûlî, senin zulmünden kime şikâyet etsin? Bu diyarda senden başka padişah var mı?"}, {"fa": "من نگویم چون قدت سروی ز بستان برنخاست\nخاست اما فتنه انگیز و خرامان برنخاست", "tr": "Ben “bahçelerde endamın gibi bir servi yetişmedi” demiyorum. Yetişti; ama böyle fitneler koparanı, böyle salına salına yürüyeni yetişmedi."}, {"fa": "کی نمودی قد که هر سو فتنه بالا نشد\nکی گشودی رخ که از هر گوشه افغان برنخاست", "tr": "Nerede endamını gösterdin ki orada fitne kopmadı (yükselmedi); nerede yüzünü açtın ki derhal her köşede feryad ü figan yükselmedi."}, {"fa": "مرده ام بی او چه سان بر آه من سوزد دلش\nکی کند در دل اثر آهی که از جان برنخاست", "tr": "Ondan ayrı ben yaşamıyorum; ölmüşüm. Nasıl benim âhım onun gönlünü yakabilir? Candan kopmayan bir âh gönüle tesir eder mi?"}, {"fa": "می کشم از دل خدنگش را و زو خون می چکد\nهمدمی ننشست پهلویم که گریان برنخاست", "tr": "Gönlümden okunu çekiyorum. Oktan kan damlıyor. Yanıma (kaburgalarıma) bir dost oturmadı ki ağlayarak kalkıp gitmesin."}, {"fa": "صبر بر نادیدنت رحمیست بر عالم ز من\nزانکه چشمی بر تو نگشادم که طوفان برنخاست", "tr": "Ben âleme acıyorum da sabrediyorum, sana bakmıyorum. Zira seni görmek için bir kere gözümü açmadım ki tufan gibi âlemi gözyaşına gark etmiş olmayayım."}, {"fa": "ظلم اشکم بین که تا گردیم با هم ساعتی\nگردبادی هم بآهم زین بیابان برنخاست", "tr": "Bak şu gözyaşımın zulmüne ki onunla beraber bulunduğumuz bir saat zarfında benim âhım bu çölde bir kasırga bile koparmadı."}, {"fa": "از سر کویت فضولی گر نخیزد دور نیست\nهیچ جا افتاده رفتار خوبان برنخاست", "tr": "Fuzûlî, senin diyarından kalkıp gitmezse buna hayret etme. Güzellerin yürüyüşüne düşkün olanlar bellerini doğrultamazlar."}];
